              "dataField": "nroundingdigits",
              "width": "200px",
              "mandatory": true,
              "mandatoryLabel": "IDS_ENTER",
              "controlType": "textbox"
            }, {
              "idsName": "IDS_UNIT",
              "dataField": "nunitcode",
              "width": "200px",
              "mandatory": true,
              "mandatoryLabel": "IDS_SELECT",
              "controlType": "selectbox"
            });
          } else if (selectedRecord["nparametertypecode"].value === parameterType.PREDEFINED) {
            mandatoryFields.push({
              "idsName": "IDS_CODEDRESULT",
              "dataField": "spredefinedname",
              "width": "200px",
              "mandatory": true,
              "mandatoryLabel": "IDS_ENTER",
              "controlType": "textbox"
            }, {
              "idsName": "IDS_ACTUALRESULT",
              "dataField": "ngradecode",
