export function getEditRegistrationComboService(inputParam, columnList, selectedRecord1, childColumnList, comboComponents,
    withoutCombocomponent) {
    return function (dispatch) {

        dispatch(initRequest(true));
        const { userInfo, operation } = { ...inputParam };

        const npreregno = inputParam.mastertoedit[inputParam.primaryKeyName];
        let urlArray = [];

        const timeZoneService = rsapi.post("timezone/getTimeZone");
        const actualService = rsapi.post("dynamicpreregdesign/getComboValues", {
            parentcolumnlist: columnList,
            childcolumnlist: childColumnList,
            userinfo: userInfo
        })
        const selectedRegistration = rsapi.post("/registration/getEditRegistrationDetails", { ...inputParam.editRegParam, npreregno })

        urlArray = [timeZoneService, actualService, selectedRegistration]

        Axios.all(urlArray)
            .then(response => {
                let selectedRecord = { ...response[2].data["SelectedRegistration"][0] };
                selectedRecord = { ...selectedRecord, ...selectedRecord['jsondata'] }
