                                    //   childColumnList, withoutCombocomponent)\
                                    comboValues = childComboLoad(x, comboData, selectedRecord1,
                                        childColumnList, withoutCombocomponent)
                                } else {
                                    comboValues = {
                                        "comboData": comboData,
                                    }
                                }
                            } else {
                                comboValues = {
                                    comboData: comboData,
                                    ...comboValues
                                }
                            }
                        })
                    }
                    else {
                        comboValues = {
                            "comboData": comboData,
                        }
                    }

                    let { Specification, AgaramTree, ActiveKey, FocusKey, OpenNodes } = [];
                    const selectedSpec = {}
                    Specification = constructOptionList(response[2].data["Specification"] || [], "nallottedspeccode",
                        "sspecname", undefined, undefined, true).get("OptionList");
                    AgaramTree = response[2].data["AgaramTree"];
                    ActiveKey = response[2].data["FocusKey"];
                    FocusKey = response[2].data["ActiveKey"];
