                            operation: "update"
                            //  regRecordToEdit:recordToEdit

                        }
                    })
                }
            })
            .catch(error => {
                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
                if (error.response.status === 500) {
                    toast.error(error.message);
                } else {
                    toast.warn(intl.formatMessage({
                        id: error.response.data
                    }));
                }
            })

    }
