                },
                {
                    "label": "IDS_DESIGNTEMPLATE",
                    "value": this.props.Login.masterData.realDesignTemplateMappingValue ? this.props.Login.masterData.realDesignTemplateMappingValue.sregtemplatename || "NA" :
                             this.props.Login.masterData.realDesignTemplateMappingValue ? this.props.Login.masterData.DesignTemplateMappingValue.sregtemplatename : "NA"

                },
                {
                    "label": "IDS_TESTSTATUS",
                    "value": this.props.Login.masterData.realFilterStatusValue ? this.props.Login.masterData.realFilterStatusValue.stransdisplaystatus || "NA" :
                             this.props.Login.masterData.FilterStatusValue ? this.props.Login.masterData.FilterStatusValue.stransdisplaystatus || "NA" : "NA"
                },
                // {
                //     "label": "IDS_CONFIGVERSION",
                //     "value": this.props.Login.masterData.realApprovalVersionValue ? this.props.Login.masterData.realApprovalVersionValue.sversionname || "NA" :
                //              this.props.Login.masterData.ApprovalVersionValue ? this.props.Login.masterData.ApprovalVersionValue.sversionname || "NA" : "NA"
                // },
                {
                    "label": "IDS_SECTION",
                    "value": this.props.Login.masterData.realUserSectionValue ? this.props.Login.masterData.realUserSectionValue.ssectionname || "NA" :
                             this.props.Login.masterData.UserSectionValue ? this.props.Login.masterData.UserSectionValue.ssectionname || "NA" : "NA"
                },
