import rsapi from '../rsapi';
import Axios from 'axios';
import {toast} from 'react-toastify';
import { DEFAULT_RETURN} from './LoginTypes';
import { initRequest } from './LoginAction';
import { sortData ,constructOptionList,rearrangeDateFormat,formatInputDate } from '../components/CommonScript';
import { transactionStatus } from '../components/Enumeration';
import { intl } from "../components/App"; 

export const getreloadQuotation = (inputParam) => {
    return (dispatch) => {
        dispatch(initRequest(true));
        rsapi.post("/quotation/get" + inputParam.methodUrl, inputParam.inputData)
            .then(response => {
                const masterData = response.data
                sortData(masterData);
                    
                dispatch({
                    type: DEFAULT_RETURN,
                    payload: {
                        loading: false,
                        dataState: undefined,
                        masterData: {
                            ...masterData,
                            // quotation: inputParam.inputData.quotation
                            
                        }
                    }
                });
            })
            .catch(error => {
                dispatch({
                    type: DEFAULT_RETURN,
                    payload: {
                        loading: false
                    }
                });
                toast.error(error.message);
            });
    }
}

export function getClientCategoryComboService(methodParam,selectedRecord) {

    return function (dispatch) {

        let URL = [];

            URL=rsapi.post("/client/getClientByCategory", { "userinfo": methodParam.inputData.userinfo, "nclientcatcode":  methodParam.inputData.primarykey  })

         dispatch(initRequest(true));

        Axios.all([URL])

            .then(response => {

                let Client,sclientsiteaddress;
                let SelectedClient=[];
             

                const usertMap = constructOptionList(response[0].data.Client || [], "nclientcode","sclientname", undefined, undefined, false);              

                Client = usertMap.get("OptionList");

                // SelectedClient=response[0].data.selectedClient;
               //  sclientsiteaddress= response[0].data.selectedClientSite.saddress1;
                // selectedRecord={...selectedRecord,...response[0].data.selectedClient}
                // selectedRecord={...selectedRecord,...response[0].data.selectedClientSite.saddress1} 

                if(Client.length>0){
                    
                    selectedRecord["nclientcode"]=  {
                        label: response[0].data.selectedClient.sclientname,
                        value: response[0].data.selectedClient.nclientcode,
                        item: response[0].data.selectedClient
                    }
                    // selectedRecord["nclientcode"]=response[0].data.selectedClient.nclientcode;
                    // selectedRecord["sclientname"]=response[0].data.selectedClient.sclientname;
                    let ClientSite = response[0].data.ClientSite;
                    let ActiveClientSite = ClientSite.filter(x => x.ndefaultstatus === 3);

                    selectedRecord["sclientsiteaddress"]=ActiveClientSite[0].saddress1;
                    // selectedRecord={response[0].data.selectedClientSite.saddress1}
                }
                
               selectedRecord = {...selectedRecord}

                dispatch({ type: DEFAULT_RETURN, payload:

                     { Client,selectedRecord, loading: false, data: undefined, dataState: undefined,sclientsiteaddress,SelectedClient } })

            })

            .catch(error => {

                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })

                if (error.response.status === 500) {

                    toast.error(error.message);

                }

                else {

                    toast.warn(error.response.data);

                }

            })

    }

}

export function getQuotaionClientComboService(methodParam,selectedRecord) {

    return function (dispatch) {

        let URL = [];

            URL=rsapi.post("/client/getSelectedClientDetail", { "userinfo": methodParam.inputData.userinfo, "nclientcode":  methodParam.inputData.primarykey  })

         dispatch(initRequest(true));

        Axios.all([URL])

            .then(response => {

                let sclientsiteaddress;
                let SelectedClient=[];

                // SelectedClient=response[0].data.selectedClient;
                // sclientsiteaddress= response[0].data.selectedClientSite.saddress1;
                selectedRecord["sclientname"]=response[0].data.selectedClient.sclientname;
                selectedRecord["sclientsiteaddress"]=response[0].data.selectedClientSite.saddress1;

                dispatch({ type: DEFAULT_RETURN, payload:

                     { selectedRecord, loading: false, data: undefined, dataState: undefined,sclientsiteaddress,SelectedClient } })

            })

            .catch(error => {

                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })

                if (error.response.status === 500) {

                    toast.error(error.message);

                }

                else {

                    toast.warn(error.response.data);

                }

            })

    }

}

export function getProjectTypeComboService(methodParam,selectedRecord) {

