DynamicGridItem=dynamicColumn.sampledisplayfields?dynamicColumn.sampledisplayfields:[];DynamicTestGridItem=dynamicColumn.testListFields.releasetestfields?dynamicColumn.testListFields.releasetestfields:[];DynamicSubSampleGridItem=dynamicColumn.subsamplelistitem?dynamicColumn.subsamplelistitem:[];DynamicGridMoreField=dynamicColumn.samplegridmoreitem?dynamicColumn.samplegridmoreitem:[];SingleItem=dynamicColumn.sampledisplayfields?dynamicColumn.sampledisplayfields:[];testMoreField=dynamicColumn.testlistmoreitems?dynamicColumn.testlistmoreitems:[];testListColumns=dynamicColumn.testlistitem?dynamicColumn.testlistitem:[];sampleSearchField=dynamicColumn.samplesearchfields?dynamicColumn.samplesearchfields:[];subsampleSearchField=dynamicColumn.subsamplesearchfields?dynamicColumn.subsamplesearchfields:[];testSearchField=dynamicColumn.testsearchfields?dynamicColumn.testsearchfields:[];sampleSearchField.map(function(item){combinedSearchField.push(item);});subsampleSearchField.map(function(item){combinedSearchField.push(item);});testSearchField.map(function(item){combinedSearchField.push(item);});bool=true;var obj=convertDateValuetoString(this.props.Login.masterData.fromDate,this.props.Login.masterData.toDate,this.props.Login.userInfo);selectedRecord['fromDate']=obj.fromDate;selectedRecord['toDate']=obj.toDate;bool=true;}if(this.props.Login.masterData.DesignTemplateMapping!==previousProps.Login.masterData.DesignTemplateMapping){var DesignTemplateMappingMap=constructOptionList(this.props.Login.masterData.DesignTemplateMapping||[],"ndesigntemplatemappingcode","sregtemplatename",undefined,undefined,false);stateDynamicDesign=DesignTemplateMappingMap.get("OptionList");}if(bool){bool=false;this.setState({userRoleControlRights:userRoleControlRights,controlMap:controlMap,sampleListColumns:sampleListColumns,subSampleListColumns:subSampleListColumns,testListColumns:testListColumns,SingleItem:SingleItem,sampleListMainField:sampleListMainField,subSampleListMainField:subSampleListMainField,te