            .then(response => {
                let returnObj = { ...comboData, ...response.data }
                returnObj = parentChildComboLoad(parentListWithReadonly, returnObj, selectedRecord, childcolumnlist, withoutCombocomponent, inputParem, userinfo.slanguagetypecode, userinfo)
                selectedRecord = { ...selectedRecord, ...returnObj.selectedRecord }
