            rsapi.post('dynamicpreregdesign/getExternalportalDetail', {
                nexternalordercode:comboData.value,
                nquerybuildertablecode:selectedControl.table.item.nquerybuildertablecode,
                userinfo,
            })
                .then(response => {
                    let Map1=new Map();
