                                                               <FormattedMessage id={menu.sdisplayname}
                                                                   defaultMessage={menu.sdisplayname} />
                                                           </SidebarBrandTxt>
                                                       </SidebarBrand>
                                                       <PerfectScrollbar className="sidebar-scroll">
