export function getReactInputFields(userinfo, operation, respObj) {
  return function (dispatch) {
    dispatch(initRequest(true));
    const listURL = [];
    listURL[0] = rsapi.post("dynamicpreregdesign/getReactComponents", {
      userinfo
    });
    listURL[1] = rsapi.post("dynamicpreregdesign/getReactInputFields", {
      userinfo
    });
    Axios.all(listURL).then(response => {
      respObj = respObj ? { ...respObj
      } : {};
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          ReactInputFields: response[1].data,
          ReactComponents: response[0].data.components,
          ReactTables: constructOptionList(response[0].data.tables, 'stablename', 'displayname').get("OptionList"),
          selectedFieldRecord: {},
          openModal: false,
          loading: false,
          openPortal: true,
          operation,
          ...respObj
        }
      });
    }).catch(error => {
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          loading: false
        }
      });

      if (error.response.status === 500) {
        toast.error(error.message);
      } else {
        toast.warn(error.response.data);
      }
    });
  };
}
