import rsapi from '../rsapi';
import { DEFAULT_RETURN } from './LoginTypes';
import { toast } from 'react-toastify';
import { initRequest } from './LoginAction';
import { sortData, constructjsonOptionList, constructOptionList, parentChildComboLoad } from '../components/CommonScript';
import { getValidComponent, replaceChildFromChildren } from '../components/droparea/helpers';
import { ReactComponents, transactionStatus } from '../components/Enumeration';
import Axios from 'axios';
import { getTreeByProduct } from './RegistrationAction';

export function getReactInputFields(userinfo, operation, respObj) {

    return function (dispatch) {
        dispatch(initRequest(true));
        const listURL = [];
        listURL[0] = rsapi.post("dynamicpreregdesign/getReactComponents", { userinfo })
        listURL[1] = rsapi.post("dynamicpreregdesign/getReactInputFields", { userinfo })
        Axios.all(listURL)
            .then(response => {
                respObj = respObj ? { ...respObj } : {}
                dispatch({
                    type: DEFAULT_RETURN,
                    payload: {
                        ReactInputFields: response[1].data,
                        ReactComponents: response[0].data.components,
                        ReactTables: constructOptionList(response[0].data.tables, 'stablename', 'displayname').get("OptionList"),
                        selectedFieldRecord: {},
                        openModal: false,
                        loading: false,
                        openPortal: true,
                        operation,
                        ...respObj
                    }
                })
            })
            .catch(error => {
                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
                if (error.response.status === 500) {
                    toast.error(error.message);
                } else {
                    toast.warn(error.response.data);
                }
            })
    }
}
export function selectRegistrationTemplate(template, masterData, userinfo) {
    return function (dispatch) {
        dispatch(initRequest(true));
        rsapi.post("dynamicpreregdesign/getRegistrationTemplateById", {
            nreactregtemplatecode: template.nreactregtemplatecode,
            userinfo
        })
            .then(response => {

                dispatch({
                    type: DEFAULT_RETURN,
                    payload: { masterData: { ...masterData, selectedTemplate: response.data }, loading: false }
                })
