                      data-tip={this.props.intl.formatMessage({ id: "IDS_EDIT" })}
                      onClick={(e) => {
                        e.stopPropagation();
                        this.editRecord(e, props.item);
                      }}
                    ></span>
                    {!props.item.isRoot ? (
                      <span
                        className="k-icon k-i-delete ml-2"
                        data-tip={this.props.intl.formatMessage({ id: "IDS_DELETE" })}
                        onClick={(e) => {
                          e.stopPropagation();
                          this.deleteNode(props.item);
                        }}
                      ></span>
                    ) : null}
                  </>
                ) : null}
              </span>
            </>
          ) : (
            ""
          )}
        </>
      );
    }
  };


