import React from 'react';
import { Col, Row } from 'react-bootstrap';
import { injectIntl } from 'react-intl';
import DropZone from '../../components/dropzone/dropzone.component';
import { deleteAttachmentDropZone, onDropAttachFileList } from '../../components/CommonScript';
import { connect } from 'react-redux';
import FormTextarea from '../../components/form-textarea/form-textarea.component';
import FormSelectSearch from '../../components/form-select-search/form-select-search.component';

const mapStateToProps = state => {
    return ({ Login: state.Login })
}

class AddOOSWorkFlowTransaction extends React.Component {
    constructor(props) {
        super(props)
        const dataState = {
            skip: 0,
            take: this.props.settings ? parseInt(this.props.settings[14]) : 5,
        };
        this.state = {
            loading: false,
            dataState: dataState,
            openModal: false,
            inputParam: this.props.inputParam,
            loadEsign: false,
            operation: this.props.operation ? this.props.operation : "",
            selectedRecord: {
                ...this.props.selectedRecord,
                noosworkflowtypecode: this.props.noosworkflowtypecode,
                ntranscode: this.props.ntranscode,
                scomments: "",
                sadditionalcomments: "",
                sfilename: undefined
            },
            OosStepStatus: this.props.OosStepStatus,
            OosWorkFlowType: this.props.OosWorkFlowType,
            OosMaster: this.props.OosMaster
        }
    }

    render() {
        return (
            <>
                <Row>
                    <Col md={12}>
                        <FormSelectSearch
                            formLabel={this.props.intl.formatMessage({ id: "IDS_STEPNAME" })}
                            isSearchable={true}
                            name={"noosworkflowtypecode"}
                            isDisabled={false}
                            placeholder={this.props.intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                            isMandatory={true}
                            options={this.state.OosWorkFlowType ? this.state.OosWorkFlowType : []}
                            optionId='noosworkflowtypecode'
                            optionValue='soosworkflowtypename'
                            value={this.state.selectedRecord ? this.state.selectedRecord.noosworkflowtypecode ? this.state.selectedRecord.noosworkflowtypecode : "" : ""}
                            onChange={(event) => this.onComboChange(event, 'noosworkflowtypecode')}
                            closeMenuOnSelect={true}
                            alphabeticalSort={true}
                        />
                    </Col>
                    <Col md={12}>
                        <FormTextarea
                            label={this.props.intl.formatMessage({ id: "IDS_COMMENTS" })}
                            name={"scomments"}
                            onChange={(event) => this.onInputOnChange(event, "")}
                            placeholder={this.props.intl.formatMessage({ id: "IDS_COMMENTS" })}
                            value={this.state.selectedRecord["scomments"] || ""}
                            rows="2"
                            isMandatory={true}
                            required={true}
                            maxLength={4000}
                            isDisabled={false}
                        />
                    </Col>
                    <Col md={12}>
                        <FormTextarea
                            label={this.props.intl.formatMessage({ id: "IDS_ADDITIONALCOMMENTS" })}
                            name={"sadditionalcomments"}
                            onChange={(event) => this.onInputOnChange(event)}
                            placeholder={this.props.intl.formatMessage({ id: "IDS_ADDITIONALCOMMENTS" })}
                            value={this.state.selectedRecord["sadditionalcomments"] || ""}
                            rows="2"
                            isMandatory={false}
                            required={false}
                            maxLength={4000}
                            isDisabled={false}
                        />
                    </Col>
                    <Col md={12}>
                        <FormSelectSearch
                            formLabel={this.props.intl.formatMessage({ id: "IDS_STATUS" })}
                            isSearchable={true}
                            name={"ntranscode"}
                            isDisabled={false}
                            placeholder={this.props.intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                            isMandatory={true}
                            options={this.state.OosStepStatus ? this.state.OosStepStatus : []}
                            optionId='ntranscode'
                            optionValue='stransdisplaystatus'
                            value={this.state.selectedRecord ? this.state.selectedRecord.ntranscode ? this.state.selectedRecord.ntranscode : "" : ""}
                            onChange={(event) => this.onComboChange(event, 'ntranscode')}
                            closeMenuOnSelect={true}
                            alphabeticalSort={true}
                        />
                    </Col>
                    <Col md={12}>
                        <DropZone
                            name='AttachmentFile'
                            label={this.props.intl.formatMessage({ id: "IDS_FILE" })}
                            isMandatory={false}
                            maxFiles={1}
                            minSize={0}
                            maxSize={this.props.maxSize}
                            onDrop={(event) => this.onDrop(event, 'sfilename', 1)}
                            deleteAttachment={this.deleteAttachment}
                            actionType={this.actionType}
                            fileNameLength={this.fileNameLength}
                            editFiles={this.state.selectedRecord ? this.state.selectedRecord : {}}
                            attachmentTypeCode={this.state.selectedRecord && this.state.selectedRecord.selectedAttachmentFile && this.state.selectedRecord.selectedAttachmentFile.nattachmenttypecode}
                            fileSizeName="nfilesize"
                            fileName="sfilename"
                        />
                    </Col>
                </Row>
            </>
        );
    }

    onComboChange = (comboData, fieldName) => {
        let selectedRecord = this.state.selectedRecord || {};
        selectedRecord[fieldName] = comboData;
        this.setState({ selectedRecord });
        this.props.childDataChange(selectedRecord);
    }

    onInputOnChange = (event) => {
        const selectedRecord = this.state.selectedRecord || {};
        selectedRecord[event.target.name] = event.target.value;
        this.setState({ selectedRecord })
        this.props.childDataChange(selectedRecord);
    }

    onDrop = (attachedFiles, fieldName, maxSize) => {
        let selectedRecord = this.state.selectedRecord || {};
        selectedRecord[fieldName] = onDropAttachFileList(selectedRecord[fieldName], attachedFiles, maxSize)
        this.setState({ selectedRecord });
        this.props.childDataChange(selectedRecord);
    }

    deleteAttachment = (event, file, fieldName) => {
        let selectedRecord = this.state.selectedRecord || {};
        selectedRecord[fieldName] = deleteAttachmentDropZone(selectedRecord[fieldName], file)
        this.setState({
            selectedRecord
        });
        this.props.childDataChange(selectedRecord);
    }
}

export default connect(mapStateToProps, {})(injectIntl(AddOOSWorkFlowTransaction));
