import React from 'react';

import DateTimePicker from '../../components/date-time-picker/date-time-picker.component';
import { Row, Col } from 'react-bootstrap';
import FormSelectSearch from '../../components/form-select-search/form-select-search.component';
import { injectIntl } from 'react-intl';

const RegistrationFilter = (props) => {
    return (
        <Row>
            <Col md={6}>
                <DateTimePicker
                    name={"fromdate"}
                    label={props.intl.formatMessage({ id: "IDS_FROM" })}
                    className='form-control'
                    placeholderText="Select date.."
                    selected={props.FromDate}
                    dateFormat={props.userInfo["ssitedate"]}
                    isClearable={false}
                    onChange={date => props.handleFilterDateChange("fromdate", date)}
                    value={props.FromDate}

                />
            </Col>
            <Col md={6}>
                <DateTimePicker
                    name={"todate"}
                    label={props.intl.formatMessage({ id: "IDS_TO" })}
                    className='form-control'
                    placeholderText="Select date.."
                    selected={props.ToDate}
                    dateFormat={props.userInfo["ssitedate"]}
                    isClearable={false}
                    onChange={date => props.handleFilterDateChange("todate", date)}
                    value={props.ToDate}

                />
            </Col>
            <Col md={12}>
                <FormSelectSearch
                    formLabel={props.intl.formatMessage({ id: "IDS_SAMPLETYPE" })}
                    isSearchable={true}
                    name={"nsampletypecode"}
                    isDisabled={false}
                    placeholder={props.intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                    isMandatory={true}
                    options={props.SampleType}
                    optionId="nsampletypecode"
                    optionValue="ssampletypename"
                    value={props.SampleTypeValue ? { "label": props.SampleTypeValue.ssampletypename, "value": props.SampleTypeValue.ssampletypename } : ""}
                    showOption={true}
                    sortField="nsorter"
                    sortOrder="ascending"
                    onChange={(event) => props.onSampleTypeChange(event, 'nsampletypecode', 'SampleTypeValue')}
                >
                </FormSelectSearch>
            </Col>
            <Col md={12}>
                <FormSelectSearch
                    formLabel={props.intl.formatMessage({ id: "IDS_REGISTRATIONTYPE" })}
                    isSearchable={true}
                    name={"nregtypecode"}
                    isDisabled={false}
                    placeholder={props.intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                    isMandatory={true}
                    options={props.RegistrationType}
                    optionId="nregtypecode"
                    optionValue="sregtypename"
                    value={props.RegTypeValue ? { "label": props.RegTypeValue.sregtypename, "value": props.RegTypeValue.nregtypecode } : ""}
                    showOption={true}
                    sortField="nsorter"
                    sortOrder="ascending"
                    onChange={(event) => props.onRegTypeChange(event, 'nregtypecode', 'RegTypeValue')}
                >
                </FormSelectSearch>
            </Col>
            <Col md={12}>
                <FormSelectSearch
                    formLabel={props.intl.formatMessage({ id: "IDS_REGISTRATIONSUBTYPE" })}
                    isSearchable={true}
                    name={"nregsubtypecode"}
                    isDisabled={false}
                    placeholder={props.intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                    isMandatory={true}
                    options={props.RegistrationSubType}
                    optionId="nregsubtypecode"
                    optionValue="sregsubtypename"
                    value={props.RegSubTypeValue ? { "label": props.RegSubTypeValue.sregsubtypename, "value": props.RegSubTypeValue.nregsubtypecode } : ""}
                    showOption={true}
                    sortField="nsorter"
                    sortOrder="ascending"
                    onChange={(event) => props.onRegSubTypeChange(event, 'nregsubtypecode', 'RegSubTypeValue')}
                >
                </FormSelectSearch>
            </Col> 
            {props.RegSubTypeValue.nneedtemplatebasedflow?
            <Col md={12}>
                <FormSelectSearch
                    formLabel={props.intl.formatMessage({ id: "IDS_DESIGNTEMPLATE" })}
                    isSearchable={true}
                    name={"ndesigntemplatemappingcode"}
                    isDisabled={false}
                    placeholder={props.intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                    isMandatory={true}
                    options={props.DynamicDesignMapping}
                    optionId="ndesigntemplatemappingcode"
                    optionValue="sregtemplatename"
                    value={props.DesignTemplateMappingValue ? { "label": props.DesignTemplateMappingValue.sregtemplatename, "value": props.DesignTemplateMappingValue.ndesigntemplatemappingcode } : ""}
                    showOption={true}
                    sortField="ndesigntemplatemappingcode"
                    sortOrder="ascending"
                    onChange={(event) => props.onDesignTemplateChange(event, 'ndesigntemplatemappingcode', 'DesignTemplateMappingValue')}
                >
                </FormSelectSearch>
            </Col>
            :""}
            <Col md={12}>
                <FormSelectSearch
                    formLabel={props.intl.formatMessage({ id: "IDS_FILTERSTATUS" })}
                    isSearchable={true}
                    name={"ntransactionstatus"}
                    isDisabled={false}
                    placeholder={props.intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                    isMandatory={true}
                    options={props.FilterStatus}
                    optionId="ntransactionstatus"
                    optionValue="stransdisplaystatus"
                    value={props.FilterStatusValue ? { "label": props.FilterStatusValue.stransdisplaystatus, "value": props.FilterStatusValue.ntransactionstatus } : ""}
                    showOption={true}
                    sortField="stransdisplaystatus"
                    sortOrder="ascending"
                    onChange={(event) => props.onFilterChange(event, 'FilterStatusValue')}
                >
                </FormSelectSearch>
            </Col>
        </Row>
    );
}
export default injectIntl(RegistrationFilter);
