import React from 'react';
import { Row, Col } from 'react-bootstrap';
import { injectIntl } from 'react-intl';
import { intl } from '../../components/App';
import { MediaHeader, MediaLabel } from '../../components/App.styles';
import { ListWrapper } from '../../components/client-group.styles';
import FormNumericInput from '../../components/form-numeric-input/form-numeric-input.component';
import FormTextarea from '../../components/form-textarea/form-textarea.component';
import TestPopOver from './TestPopOver';
const ResultEntryInstrumentForm = (props) => {
    let stestsynonyms = props.selecteRecord.stestsynonym ? props.selecteRecord.stestsynonym.split(",") : []

    let message= `${stestsynonyms.length} ${props.intl.formatMessage({ id: "IDS_TESTS" })} ${props.intl.formatMessage({ id: "IDS_SELECTED" })}`
      return (
        <>
            
            {Object.values(props.selecteRecord).length > 0 ?
                <div>
                    <Row className="mb-4">
                        <Col md={12}>
                            <MediaHeader className={`labelfont`}>
                                {stestsynonyms.length === 1 ?
                                        `${props.intl.formatMessage({id:"IDS_TEST"})}: ${stestsynonyms[0] }`:
                                        // <span 
                                        //     onMouseOver={event => tooltip && tooltip.handleMouseOver(event)}
                                        //     onMouseOut={event => tooltip && tooltip.handleMouseOut(event)} //title={()=>tittleContent(stestsynonyms)}
                                        //     title={stestsynonyms}>
                                        //     {stestsynonyms.length}{" "}
                                        //     {props.intl.formatMessage({id:"IDS_TESTS"})}{" "}
                                        //     {props.intl.formatMessage({id:"IDS_SELECTED"})}
                                        // </span>
                                        <TestPopOver stringList={stestsynonyms} message={message}></TestPopOver>

                                }
                            </MediaHeader>
                        </Col>
                    </Row>
                </div>
                : ""}

            <Row>

                <Col md={8}>
                    <FormNumericInput
                        name="numeric"
                        type="number"
                        // placeholder={intl.formatMessage({ id: "IDS_PREANALYSISTIME" })}
                        isMandatory={true}
                        label={props.intl.formatMessage({ id: "IDS_PREANALYSISTIME" })}
                        value={Object.values(props.selecteRecord).length > 0 ? props.selecteRecord.spreanalysistime : ""}
                        strict={true}
                        maxLength={6}
                        noStyle={true}
                        required={true}
                        className="form-control"
                        onChange={(event) => props.onNumericChange(event, "spreanalysistime")}
                    />
                </Col>
                <Col md={2}>
                    {/* {this.props.selectedResultGrade.length > 0 ? */}
                    <ListWrapper><MediaLabel className="labelfont" >
                        {props.intl.formatMessage({ id: "IDS_MINUTES" })}</MediaLabel></ListWrapper>
                    {/* : ""} */}
                </Col>

                <Col md={8}>
                    <FormNumericInput
                        name="numeric"
                        type="number"
                        isMandatory={true}
                        // placeholder={intl.formatMessage({ id: "IDS_PREPARATIONTIME" })}
                        label={props.intl.formatMessage({ id: "IDS_PREPARATIONTIME" })}
                        value={Object.values(props.selecteRecord).length > 0 ? props.selecteRecord.spreparationtime : ""}
                        strict={true}
                        maxLength={6}
                        noStyle={true}
                        className="form-control"
                        onChange={(event) => props.onNumericChange(event, "spreparationtime")}
                    />
                </Col>
                <Col md={2}>
                    {/* {this.props.selectedResultGrade.length > 0 ? */}
                    <ListWrapper><MediaLabel className="labelfont" >
                        {props.intl.formatMessage({ id: "IDS_MINUTES" })}</MediaLabel></ListWrapper>
                    {/* : ""} */}
                </Col>

                <Col md={8}>
                    <FormNumericInput
                        name="numeric"
                        type="number"
                        isMandatory={true}
                        // placeholder={intl.formatMessage({ id: "IDS_ANALYSISTIME" })}
                        label={props.intl.formatMessage({ id: "IDS_ANALYSISTIME" })}
                        value={Object.values(props.selecteRecord).length > 0 ? props.selecteRecord.sanalysistime : ""}
                        strict={true}
                        maxLength={6}
                        noStyle={true}
                        className="form-control"
                        onChange={(event) => props.onNumericChange(event, "sanalysistime")}
                    />
                </Col>
                <Col md={2}>
                    {/* {this.props.selectedResultGrade.length > 0 ? */}
                    <ListWrapper><MediaLabel className="labelfont" >
                        {props.intl.formatMessage({ id: "IDS_MINUTES" })}</MediaLabel></ListWrapper>
                    {/* : ""} */}
                </Col>
                <Col md={8}>
                    <FormNumericInput
                        name="numeric"
                        type="number"
                        isMandatory={true} 
                        // placeholder={intl.formatMessage({ id: "IDS_MISCTIME" })}
                        label={props.intl.formatMessage({ id: "IDS_MISCTIME" })}
                        value={Object.values(props.selecteRecord).length > 0 ? props.selecteRecord.smisctime : ""}
                        strict={true}
                        maxLength={6}
                        noStyle={true}
                        className="form-control"
                        onChange={(event) => props.onNumericChange(event, "smisctime")}
                    />
                </Col>
                <Col md={2}>
                    {/* {this.props.selectedResultGrade.length > 0 ? */}
                    <ListWrapper><MediaLabel className="labelfont" >
                        {props.intl.formatMessage({ id: "IDS_MINUTES" })}</MediaLabel></ListWrapper>
                    {/* : ""} */}
                </Col>
                <Col md={8}>
                    <FormTextarea
                        name={"Result"}
                        placeholder={intl.formatMessage({ id: "IDS_COMMENTS" })}
                        label={intl.formatMessage({ id: "IDS_COMMENTS" })}
                        type="text"
                        defaultValue={Object.values(props.selecteRecord).length > 0 ? props.selecteRecord.scomments : ""}
                        isMandatory={false}
                        required={false}
                        maxLength={100}
                        onChange={(event) => props.onInputChange(event, "scomments")}
                    />
                </Col>

            </Row>
        </>
    )
}
export default injectIntl(ResultEntryInstrumentForm);
