export function getDesignTemplateMappingDetail(designtemplatemapping, userInfo, masterData) {
  return function (dispatch) {
    dispatch(initRequest(true));
    return rsapi.post("designtemplatemapping/getDesignTemplateMapping", {
      ndesigntemplatemappingcode: designtemplatemapping.ndesigntemplatemappingcode,
      nsampletypecode: designtemplatemapping.nsampletypecode,
      nregtypecode: designtemplatemapping.nregtypecode,
      nregsubtypecode: designtemplatemapping.nregsubtypecode,
      userinfo: userInfo,
      nformcode: designtemplatemapping.nformcode
    }).then(response => {
      masterData = { ...masterData,
        ...response.data
      };
      sortData(masterData);
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          masterData,
          loading: false
        }
      });
    }).catch(error => {
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          loading: false
        }
      });

      if (error.response.status === 500) {
        toast.error(intl.formatMessage({
          id: error.message
        }));
      } else {
        toast.warn(intl.formatMessage({
          id: error.response
        }));
      }
    });
  };
}
export function getDesignTemplateMappingComboService(param) {
  return function (dispatch) {
    const screenName = param.screenName;
    const operation = param.operation;
    const userInfo = param.userInfo;
    const nsampletypecode = param.realSampleValue;
    const nregtypecode = param.realRegTypeValue;
    const nregsubtypecode = param.realRegSubTypeValue;
    const ncontrolCode = param.controlId;
    const moduleTypeArray = param.moduleTypeArray; //if (//(operation === "create") && (nsampletypecode != -1) && (nregtypecode != -1) && (nregsubtypecode != -1)) {

    let isValid = true;

    if (nsampletypecode !== 4) {
      if (nsampletypecode != -1 && nregtypecode != -1 && nregsubtypecode != -1) {
        isValid = true;
      } else {
        isValid = false;
      }
    }

    if (isValid) {
      const designtemplatemappingService = rsapi.post("designtemplatemapping/getDynamicPreRegDesign", {
        userinfo: userInfo,
        nsampletypecode,
        nregtypecode,
        nregsubtypecode
      });
      let urlArray = [designtemplatemappingService];
      dispatch(initRequest(true));
      Axios.all(urlArray).then(response => {
        let selectedRecord = {
          nnewmodule: moduleTypeArray[0]
        };
        let designtemplatemappingMap = constructOptionList(response[0].data.DesignTemplateMapping || [], "nreactregtemplatecode", "sregtemplatename", undefined, undefined, true);
        const designtemplatemappingList = designtemplatemappingMap.get("OptionList");
        let subSampleTemplateList = [];
        let testListFields = {};

        if (nsampletypecode !== SampleType.Masters) {
          designtemplatemappingMap = constructOptionList(response[0].data.SubSampleTemplate || [], "nreactregtemplatecode", "sregtemplatename", undefined, undefined, true);
          subSampleTemplateList = designtemplatemappingMap.get("OptionList");
          testListFields = response[0].data.TestListFields.jsondata;
        }

        const qualisModuleMap = constructOptionList(response[0].data.DT_QualisModule || [], "nmodulecode", "sdisplayname", undefined, undefined, true);
        const qualisModuleList = qualisModuleMap.get("OptionList");
        dispatch({
          type: DEFAULT_RETURN,
          payload: {
            designtemplatemappingList,
            subSampleTemplateList,
            testListFields,
            operation,
            screenName,
            selectedRecord,
            openModal: true,
            ncontrolCode,
            loading: false,
            designTemplateQualisModule: qualisModuleList
          }
        });
      }).catch(error => {
        dispatch({
          type: DEFAULT_RETURN,
          payload: {
            loading: false
          }
        });

        if (error.response.status === 500) {
          toast.error(intl.formatMessage({
            id: error.message
          }));
        } else {
          toast.warn(intl.formatMessage({
            id: error.response.data
          }));
        }
      });
    } else {
      toast.warn(intl.formatMessage({
        id: "IDS_SELECTALLVALUESINFILTER"
      }));
    }
  };
}
