                                                                >
                                                                    <FontAwesomeIcon icon={faThumbsUp} title={this.props.intl.formatMessage({ id: "IDS_APPROVE" })} />
                                                                </Nav.Link>
                                                                {/* {this.props.Login.masterData.realSampleValue && this.props.Login.masterData.realSampleValue.value !== TemplateType.Masters ? */}
                                                                    <Nav.Link className="btn btn-circle outline-grey mr-2"
                                                                        hidden={this.state.userRoleControlRights.indexOf(approveId) === -1}
                                                                        data-tip={this.props.intl.formatMessage({ id: "IDS_CONFIGUREDISPLAYFIELDS" })}
                                                                        data-for="tooltip_list_wrap"
                                                                        onClick={() => this.openFieldConfiguration(approveId, "configure")}
                                                                    >
                                                                        <FontAwesomeIcon icon={faList} title={this.props.intl.formatMessage({ id: "IDS_CONFIGUREDISPLAYFIELDS" })} />
                                                                    </Nav.Link>
                                                                     {/* : ""} */}
                                                                {this.props.Login.masterData.realSampleValue && this.props.Login.masterData.realSampleValue.value !== TemplateType.Masters ?
                                                                    <><Nav.Link className="btn btn-circle outline-grey mr-2"
                                                                            hidden={this.state.userRoleControlRights.indexOf(approveId) === -1}
                                                                            data-tip={this.props.intl.formatMessage({ id: "IDS_CONFIGUREEDITFIELDS" })}
                                                                            data-for="tooltip_list_wrap"
                                                                            onClick={() => this.editFieldConfiguration(approveId)}
                                                                        > 
                                                                            <FontAwesomeIcon icon={faList} title={this.props.intl.formatMessage({ id: "IDS_CONFIGUREEDITFIELDS" })} />
                                                                    </Nav.Link>
                                                                    {this.props.Login.masterData.realRegSubTypeValue && this.props.Login.masterData.realRegSubTypeValue.item.nneedsubsample === true ?
                                                                        <>
                                                                            <Nav.Link className="btn btn-circle outline-grey mr-2"
                                                                                        hidden={this.state.userRoleControlRights.indexOf(approveId) === -1}
                                                                                        data-tip={this.props.intl.formatMessage({ id: "IDS_CONFIGURESUBSAMPLEDISPLAYFIELDS" })}
                                                                                        data-for="tooltip_list_wrap"
                                                                                        onClick={() => this.openFieldConfiguration(approveId, 'configuresubsample')}
                                                                                    > 
                                                                                        <FontAwesomeIcon icon={faList} title={this.props.intl.formatMessage({ id: "IDS_CONFIGURESUBSAMPLEDISPLAYFIELDS" })} />
                                                                            </Nav.Link>
                                                                            {/* <Nav.Link className="btn btn-circle outline-grey mr-2"
                                                                                    hidden={this.state.userRoleControlRights.indexOf(approveId) === -1}
                                                                                    data-tip={this.props.intl.formatMessage({ id: "IDS_CONFIGURSUBSAMPLEEEDITFIELDS" })}
                                                                                    data-for="tooltip_list_wrap"
                                                                                    onClick={() => this.editFieldConfiguration(approveId)}
