}

export const changeStorageCategoryFilter = (inputParam, filterStorageCategory) => {
    return (dispatch) => {
        dispatch(initRequest(true));
        rsapi.post("/samplestoragelocation/getSampleStorageLocationByCategory", inputParam.inputData)
