import { toast } from 'react-toastify';
import Axios from 'axios';
import rsapi from '../rsapi';
import { initRequest } from './LoginAction';
import {DEFAULT_RETURN} from './LoginTypes';
import {sortData, constructOptionList, rearrangeDateFormat, ageCalculate} from '../components/CommonScript';
import {
    intl
} from '../components/App';
export function getPatientDetail (patient, userInfo, masterData) {
    return function (dispatch) {   
    dispatch(initRequest(true));
    return rsapi.post("patient/getPatient", {npatientcode:patient.npatientcode, userinfo:userInfo})
   .then(response=>{     
        masterData = {...masterData, ...response.data};       
        sortData(masterData);
        dispatch({type: DEFAULT_RETURN, payload:{masterData, operation:null, modalName:undefined, 
             loading:false}});   
   })
   .catch(error=>{
        dispatch({type: DEFAULT_RETURN, payload: {loading:false}})
       if (error.response.status === 500){
           toast.error(error.message);
       } 
       else{               
           toast.warn(error.response.data);
       }  
  
   })
}}


export function getPatientComboService(inputParam){ 
    return function (dispatch) {    
  
            const genderService = rsapi.post("patient/getGender", {userinfo:inputParam.userInfo});
            const UTCtimeZoneService = rsapi.post("timezone/getLocalTimeByZone", { userinfo: inputParam.userInfo });
                    
            let urlArray = [];
            if (inputParam.operation === "create"){
               urlArray = [genderService, UTCtimeZoneService];
            }
            else{                    
                const patientById =  rsapi.post("patient/getActivePatientById", 
                                { [inputParam.primaryKeyName] :inputParam.masterData.SelectedPatient[inputParam.primaryKeyName] , 
                                    "userinfo": inputParam.userInfo} );
                urlArray = [genderService, patientById];
            }
            dispatch(initRequest(true));
            Axios.all(urlArray)
                .then(response=>{                   
                    let gender =[];
                 
                    const genderMap = constructOptionList(response[0].data['Gender'] || [], "ngendercode",
                                          "sgendername", undefined, undefined, false);               

                    const genderList = genderMap.get("OptionList");                   
                    
                    let selectedRecord =  {};
                    const currentTime = rearrangeDateFormat(inputParam.userInfo, response[1].data);
                   
                    if (inputParam.operation === "update"){
                        selectedRecord = response[1].data;
                       
                        gender.push({"value" : response[1].data["ngendercode"], "label" : response[1].data["sgendername"]});
                                             
                        selectedRecord["ngendercode"] = gender[0];

                        if (selectedRecord["ddob"] !== null){
                            selectedRecord["ddob"] = rearrangeDateFormat(inputParam.userInfo, selectedRecord["sdob"]);
                        }                                          
                    }
                    else{        
                      
                        selectedRecord["ngendercode"] = genderMap.get("DefaultValue");  
                        selectedRecord["ddob"] = rearrangeDateFormat(inputParam.userInfo, response[1].data);
                        selectedRecord["sage"]  = ageCalculate(selectedRecord["ddob"])
                    }
                  
                    dispatch({type: DEFAULT_RETURN, payload:{genderList,                            
                                                            operation:inputParam.operation, 
                                                            screenName:inputParam.screenName,   
                                                            selectedRecord,
                                                            openModal : true,
                                                            ncontrolCode:inputParam.ncontrolcode,
                                                            loading:false,
                                                            currentTime
                                                            }});
                })
                .catch(error=>{
                    dispatch({type: DEFAULT_RETURN, payload: {loading:false}})
                    if (error.response.status === 500){
                        toast.error(error.message);
                    } 
                    else{               
                        toast.warn(error.response.data);
                    }  
                }) 
  
}}


export function getPatientReport (patient, userInfo, ncontrolCode) {
    return function (dispatch) {   
    dispatch(initRequest(true));
    return rsapi.post("patient/patientReportGenerate", {patient, userinfo:userInfo, 
                                                        ncontrolcode:ncontrolCode})
    .then(response => {
        dispatch({ type: DEFAULT_RETURN, payload: { loading: false, 
            loadEsign: false, openModal: false } })
        document.getElementById("download_data").setAttribute("href", response.data.filepath);
        document.getElementById("download_data").click();
    })
    .catch(error => {
        dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
        if (error.response.status === 500) {
            toast.error(error.message);
        }
        else {
            toast.warn(error.response.data);
        }
    }) 
}}
  
export function getPatientDetailsByFilterQuery(inputParam,masterData) {
    return function (dispatch) {
        dispatch(initRequest(true));
        rsapi.post("/patient/filterByPatient",{"filterquery":inputParam.inputData.filterquery 

            , "userinfo": inputParam.inputData.userinfo})
            .then(response => {
               let slideResult=response.data.PatientList;
               let selectedRecord=response.data.PatientList;
                masterData = {
                    ...masterData,
                    ...response.data,searchedData: undefined 
                };
                sortData(masterData);
                dispatch({
                    type: DEFAULT_RETURN, payload: {
                        masterData, loading: false, skip: 0, take: 20,slideResult:false,
                        openModal:false,openAdvFilter:false,selectedRecord
                    }
                });
            })
            .catch(error => {
                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
                toast.error(intl.formatMessage({ id: error.message }));
            });
    }
}
