import React from 'react';
import { connect } from 'react-redux';
import { toast } from 'react-toastify';
import { injectIntl, FormattedMessage } from 'react-intl';
import { Row, Col, Card, Nav, FormGroup, FormLabel} from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPencilAlt, faTrashAlt } from '@fortawesome/free-solid-svg-icons';
import DataGrid from '../../components/data-grid/data-grid.component';
import {
    callService, crudMaster, validateEsignCredential, updateStore, getPatientDetail,
    getPatientComboService, filterColumnData, getTestParameter, getPatientReport
} from '../../actions';
import { DEFAULT_RETURN } from '../../actions/LoginTypes';
import { showEsign, getControlMap, validatePhoneNumber, ageCalculate, formatInputDate } from '../../components/CommonScript';
import { transactionStatus } from '../../components/Enumeration';
import ListMaster from '../../components/list-master/list-master.component';
import SlideOutModal from '../../components/slide-out-modal/SlideOutModal';
import { ReactComponent as Reports } from '../../assets/image/report-Icon.svg'
import { ReadOnlyText, ContentPanel } from '../../components/App.styles';
import AddPatient from './AddPatient';
import Esign from '../audittrail/Esign';
import ConfirmMessage from '../../components/confirm-alert/confirm-message.component';
import ReactTooltip from 'react-tooltip';
import { process } from '@progress/kendo-data-query';


const mapStateToProps = state => {
    return ({ Login: state.Login })
}

class PatientMaster extends React.Component {
    constructor(props) {
        super(props);

        this.state = {

            masterStatus: "",
            error: "",
            selectedRecord: {},
            operation: "",
            selectedPatient: undefined,
            screenName: undefined,
            userRoleControlRights: [],
            controlMap: new Map(),
            isClearSearch: false,
            dataState:{skip:0, take:5}

        };
        this.searchRef = React.createRef();
        this.emailRef = React.createRef();
        this.confirmMessage = new ConfirmMessage();

        this.patientFieldList = ['sfirstname', 'slastname', 'sfathername',
            'sage', 'ddob', 'sphoneno', 'saddress', "snationalid"];

        this.searchFieldList = ["saddress", "sage", "sdob", "sgender",
            "sfirstname", "sfathername", "slastname", "spatientid", "sphoneno"];

        this.columnList = [{ "idsName": "IDS_ARNO", "dataField": "sarno", "width": "150px" },
                            { "idsName": "IDS_PRODUCT", "dataField": "sproductname", "width": "250px" },
                            { "idsName": "IDS_SPECIFICATION", "dataField": "sspecname", "width": "150px" },
                            { "idsName": "IDS_SPECIMENCOLLECTIONTIME", "dataField": "scolleciondate", "width": "100px" },
                            { "idsName": "IDS_RECEIVEDDATE", "dataField": "sreceiveddate", "width": "100px" },
                            { "idsName": "IDS_STATUS", "dataField": "stransdisplaystatus", "width": "100px" },];

        this.childColumnList= [{ "idsName": "IDS_TESTNAME", "dataField": "stestsynonym", "width": "250px" },
                            { "idsName": "IDS_PARAMETERNAME", "dataField": "sparametersynonym", "width": "250px" },
                            { "idsName": "IDS_RESULT", "dataField": "sresult", "width": "150px" },
                            { "idsName": "IDS_RESULTFLAG", "dataField": "sgradename", "width": "100px" },
                            { "idsName": "IDS_RESULTDATE", "dataField": "sentereddate", "width": "100px" },
                            { "idsName": "IDS_STATUS", "dataField": "stransdisplaystatus", "width": "100px" },]
    }

    static getDerivedStateFromProps(props, state) {

        if (props.Login.masterStatus !== "" && props.Login.masterStatus !== state.masterStatus) {
            toast.warn(props.Login.masterStatus);
            props.Login.masterStatus = "";
        }
        if (props.Login.error !== state.error) {
            toast.error(props.Login.error)
            props.Login.error = "";
        }
        if (props.Login.selectedRecord === undefined) {
            return { selectedRecord: {} }
        }
        return null;
    }

    render() {

        const addId = this.state.controlMap.has("AddPatient") && this.state.controlMap.get("AddPatient").ncontrolcode;
        const editId = this.state.controlMap.has("EditPatient") && this.state.controlMap.get("EditPatient").ncontrolcode;
        const deleteId = this.state.controlMap.has("DeletePatient") && this.state.controlMap.get("DeletePatient").ncontrolcode;
        const patientReportId = this.state.controlMap.has("PatientReport") && this.state.controlMap.get("PatientReport").ncontrolcode;
        
        const filterParam = {
            inputListName: "PatientList", selectedObject: "SelectedPatient", primaryKeyField: "npatientcode",
            fetchUrl: "patient/getPatient", fecthInputObject: { userinfo: this.props.Login.userInfo },
            masterData: this.props.Login.masterData, searchFieldList: this.searchFieldList
        };

        const addParam = {
            screenName: "IDS_PATIENTMASTER", operation: "create", primaryKeyName: "npatientcode",
            masterData: this.props.Login.masterData, userInfo: this.props.Login.userInfo,
            ncontrolcode: addId
        }
        const editParam = {
            screenName: "IDS_PATIENTMASTER", operation: "update", primaryKeyName: "npatientcode",
            masterData: this.props.Login.masterData, userInfo: this.props.Login.userInfo,
            ncontrolcode: editId, inputListName: "PatientList", selectedObject: "SelectedPatient"
        };
        const mandatoryFields = [
            { "idsName": "IDS_FIRSTNAME", "dataField": "sfirstname", "mandatoryLabel": "IDS_ENTER", "controlType": "textbox" },
            { "idsName": "IDS_LASTNAME", "dataField": "slastname", "mandatoryLabel": "IDS_ENTER", "controlType": "textbox" },
            { "idsName": "IDS_FATHERGUARDIANNAME", "dataField": "sfathername", "mandatoryLabel": "IDS_ENTER", "controlType": "textbox" },
            { "idsName": "IDS_DATEOFBIRTH", "dataField": "ddob", "mandatoryLabel": "IDS_SELECT", "controlType": "selectbox" },
            { "idsName": "IDS_AGE", "dataField": "sage", "mandatoryLabel": "IDS_ENTER", "controlType": "textbox" },
            { "idsName": "IDS_SEX", "dataField": "ngendercode", "mandatoryLabel": "IDS_SELECT", "controlType": "selectbox" },
            { "idsName": "IDS_ADDRESS", "dataField": "saddress", "mandatoryLabel": "IDS_ENTER", "controlType": "textbox" },
            { "idsName": "IDS_PHONENO", "dataField": "sphoneno", "mandatoryLabel": "IDS_ENTER", "controlType": "textbox" },
        ];
        return (<>
            <div className="client-listing-wrap  mtop-4 mtop-fixed-breadcrumb">
                <Row noGutters>
                    <Col md={4}>
                        <ListMaster
                            screenName={this.props.intl.formatMessage({ id: "IDS_PATIENTMASTER" })}
                            masterData={this.props.Login.masterData}
                            userInfo={this.props.Login.userInfo}
                            masterList={this.props.Login.masterData.searchedData || this.props.Login.masterData.PatientList}
                            getMasterDetail={(user) => this.props.getPatientDetail(user, this.props.Login.userInfo, this.props.Login.masterData)}
                            selectedMaster={this.props.Login.masterData.SelectedPatient}
                            primaryKeyField="npatientcode"
                            mainField="spatientname"
                            firstField="sfathername"
                            filterColumnData={this.props.filterColumnData}
                            filterParam={filterParam}
                            userRoleControlRights={this.state.userRoleControlRights}
                            addId={addId}
                            searchRef={this.searchRef}
                            reloadData={this.reloadData}
                            openModal={() => this.props.getPatientComboService(addParam)}
                            isMultiSelecct={false}
                            hidePaging={false}
                            isClearSearch={this.props.Login.isClearSearch}
                            showMicIcon={true}
                        />
                    </Col>
                    <Col md={8}>
                        <ContentPanel className="panel-main-content">
                            <Card className="border-0">
                                {this.props.Login.masterData.PatientList && this.props.Login.masterData.PatientList.length > 0 && this.props.Login.masterData.SelectedPatient ?
                                    <>
                                        <Card.Header>
                                            <ReactTooltip place="bottom" globalEventOff='click' id="tooltip_list_wrap" />
                                            <Card.Title className="product-title-main">
                                                {this.props.Login.masterData.SelectedPatient.spatientname}
                                            </Card.Title>
                                            <Card.Subtitle>
                                                <div className="d-flex product-category">
                                                    <h2 className="product-title-sub flex-grow-1"></h2>
                                                    <div className="d-inline">
                                                      <Nav.Link name="editPatient" hidden={this.state.userRoleControlRights.indexOf(editId) === -1}
                                                                className="btn btn-circle outline-grey mr-2"
                                                                data-tip={this.props.intl.formatMessage({ id: "IDS_EDIT" })}
                                                                data-for="tooltip_list_wrap"
                                                                onClick={() => this.props.getPatientComboService(editParam)}
                                                            >
                                                                <FontAwesomeIcon icon={faPencilAlt} />
                                                        </Nav.Link>

                                                        <Nav.Link name="deletePatient" className="btn btn-circle outline-grey mr-2 action-icons-wrap"
                                                            data-tip={this.props.intl.formatMessage({ id: "IDS_DELETE" })}
                                                            data-for="tooltip_list_wrap"
                                                            hidden={this.state.userRoleControlRights.indexOf(deleteId) === -1}
                                                            onClick={() => this.ConfirmDelete(deleteId)}>
                                                            <FontAwesomeIcon icon={faTrashAlt} />
                                                        </Nav.Link>

                                                        <Nav.Link className="btn btn-circle outline-grey mr-2" name="patientReport"
                                                            hidden={this.state.userRoleControlRights.indexOf(patientReportId) === -1}
                                                            onClick={() => this.props.getPatientReport(this.props.Login.masterData.SelectedPatient, this.props.Login.userInfo, patientReportId)}
                                                            data-for="tooltip_list_wrap"
                                                            data-tip={this.props.intl.formatMessage({ id: "IDS_PATIENTREPORT" })}>
                                                            <Reports className="custom_icons" width="20" height="20" />
                                                        </Nav.Link>

                                                    </div>

                                                </div>

                                            </Card.Subtitle>
                                        </Card.Header>
                                        <Card.Body>
                                            <Row>
                                                <Col md={4}>
                                                    <FormGroup>
                                                        <FormLabel><FormattedMessage id="IDS_PATIENTID" message="Patient ID" /></FormLabel>
                                                        <ReadOnlyText>{this.props.Login.masterData.SelectedPatient.spatientid}</ReadOnlyText>
                                                    </FormGroup>
                                                </Col>

                                                <Col md={4}>
                                                    <FormGroup>
                                                        <FormLabel><FormattedMessage id="IDS_NATIONALID" message="National ID" /></FormLabel>
                                                        <ReadOnlyText>{this.props.Login.masterData.SelectedPatient.snationalid}</ReadOnlyText>
                                                    </FormGroup>
                                                </Col>
                                                <Col md={4}>
                                                    <FormGroup>
                                                        <FormLabel><FormattedMessage id="IDS_DATEOFBIRTH" message="Date Of Birth" /></FormLabel>
                                                        <ReadOnlyText>
                                                            {this.props.Login.masterData.SelectedPatient.sdob === null || this.props.Login.masterData.SelectedPatient.sdob.length === 0 ? '-' :
                                                                this.props.Login.masterData.SelectedPatient.sdob}
                                                        </ReadOnlyText>
                                                    </FormGroup>
                                                </Col>

                                                <Col md={4}>
                                                    <FormGroup>
                                                        <FormLabel><FormattedMessage id="IDS_AGE" message="Age" /></FormLabel>
                                                        <ReadOnlyText>
                                                            {this.props.Login.masterData.SelectedPatient.sage === null || this.props.Login.masterData.SelectedPatient.sage.length === 0 ? '-' :
                                                                this.props.Login.masterData.SelectedPatient.sage}
                                                        </ReadOnlyText>
                                                    </FormGroup>
                                                </Col>

                                                <Col md={4}>
                                                    <FormGroup>
                                                        <FormLabel><FormattedMessage id="IDS_SEX" message="Sex" /></FormLabel>
                                                        <ReadOnlyText>
                                                            {this.props.Login.masterData.SelectedPatient.sgender === null || this.props.Login.masterData.SelectedPatient.sgender.length === 0 ? '-' :
                                                                this.props.Login.masterData.SelectedPatient.sgender}
                                                        </ReadOnlyText>
                                                    </FormGroup>
                                                </Col>

                                                <Col md={4}>
                                                    <FormGroup>
                                                        <FormLabel><FormattedMessage id="IDS_FATHERGUARDIANNAME" message="Father/Guardian Name" /></FormLabel>
                                                        <ReadOnlyText>{this.props.Login.masterData.SelectedPatient.sfathername}</ReadOnlyText>
                                                    </FormGroup>
                                                </Col>
                                                <Col md={4}>
                                                    <FormGroup>
                                                        <FormLabel><FormattedMessage id="IDS_PHONENO" message="Phone No" /></FormLabel>
                                                        <ReadOnlyText>{this.props.Login.masterData.SelectedPatient.sphoneno}</ReadOnlyText>
                                                    </FormGroup>
                                                </Col>

                                                <Col md={8}>
                                                    <FormGroup>
                                                        <FormLabel><FormattedMessage id="IDS_ADDRESS" message="Address" /></FormLabel>
                                                        <ReadOnlyText>
                                                            {this.props.Login.masterData.SelectedPatient.saddress === null || this.props.Login.masterData.SelectedPatient.saddress.length === 0 ? '-' :
                                                                this.props.Login.masterData.SelectedPatient.saddress}
                                                        </ReadOnlyText>
                                                    </FormGroup>
                                                </Col>


                                            </Row>
                                            <Row>
                                                <Col>
                                                    <DataGrid
                                                        userRoleControlRights={this.state.userRoleControlRights}
                                                        controlMap={this.state.controlMap}
                                                        primaryKeyField={"npreregno"}
                                                        data={this.props.Login.masterData && this.props.Login.masterData["PatientHistory"]}
                                                        dataResult={process(this.props.Login.masterData && (this.props.Login.masterData["PatientHistory"] || []), this.state.dataState)}
                                                        dataState={this.state.dataState}
                                                        dataStateChange={(event)=>this.setState({dataState:event.dataState})}
                                                        extractedColumnList={this.columnList}
                                                        inputParam={this.props.Login.inputParam}
                                                        userInfo={this.props.Login.userInfo}
                                                        methodUrl={this.props.Login.inputParam.methodUrl}
                                                        pageable={true}
                                                        scrollable={"scrollable"}
                                                        isActionRequired={false}
                                                        isToolBarRequired={false}
                                                        selectedId={null}
                                                        hideColumnFilter={false}
                                                        expandField={"expanded"}
                                                        handleExpandChange={this.handleExpandChange}
                                                        hasChild={true}
                                                        childColumnList={this.childColumnList}
                                                        childMappingField={"npreregno"}
                                                        childList={this.props.Login.testMap || new Map()}
                                                    />
                                                </Col>
                                            </Row>

                                        </Card.Body>
                                    </>
                                    : ""
                                }
                            </Card>
                        </ContentPanel>
                    </Col></Row>
            </div>

            {this.props.Login.openModal ?
                <SlideOutModal show={this.props.Login.openModal}
                    closeModal={this.closeModal}
                    operation={this.props.Login.operation}
                    inputParam={this.props.Login.inputParam}
                    screenName={this.props.Login.screenName}
                    onSaveClick={this.onSaveClick}
                    esign={this.props.Login.loadEsign}
                    validateEsign={this.validateEsign}
                    masterStatus={this.props.Login.masterStatus}
                    updateStore={this.props.updateStore}
                    selectedRecord={this.state.selectedRecord || {}}
                    mandatoryFields={mandatoryFields}
                    addComponent={this.props.Login.loadEsign ?
                        <Esign operation={this.props.Login.operation}
                            onInputOnChange={this.onInputOnChange}
                            inputParam={this.props.Login.inputParam}
                            selectedRecord={this.state.selectedRecord || {}}
                        />
                        : <AddPatient
                            selectedRecord={this.state.selectedRecord || {}}
                            onInputOnChange={this.onInputOnChange}
                            onComboChange={this.onComboChange}
                            onNumericInputOnChange={this.onNumericInputOnChange}
                            handleDateChange={this.handleDateChange}
                            genderList={this.props.Login.genderList || []}
                            selectedPatient={this.props.Login.masterData.SelectedPatient || {}}
                            operation={this.props.Login.operation}
                            inputParam={this.props.Login.inputParam}
                            userInfo={this.props.Login.userInfo}
                            currentTime={this.props.Login.currentTime}

                        />}
                /> : ""}
        </>
        );
    }

    componentDidUpdate(previousProps) {
        if (this.props.Login.selectedRecord !== previousProps.Login.selectedRecord) {
            this.setState({ selectedRecord: this.props.Login.selectedRecord });
        }
        if (this.props.Login.userInfo.nformcode !== previousProps.Login.userInfo.nformcode) {
            const userRoleControlRights = [];
            if (this.props.Login.userRoleControlRights) {
                this.props.Login.userRoleControlRights[this.props.Login.userInfo.nformcode] && Object.values(this.props.Login.userRoleControlRights[this.props.Login.userInfo.nformcode]).map(item =>
                    userRoleControlRights.push(item.ncontrolcode))
            }
            const controlMap = getControlMap(this.props.Login.userRoleControlRights, this.props.Login.userInfo.nformcode)
            this.setState({ userRoleControlRights, controlMap });
        }
    }

    ConfirmDelete = (deleteId) => {
        this.confirmMessage.confirm("deleteMessage", this.props.intl.formatMessage({ id: "IDS_DELETE" }), this.props.intl.formatMessage({ id: "IDS_DEFAULTCONFIRMMSG" }),
            this.props.intl.formatMessage({ id: "IDS_OK" }), this.props.intl.formatMessage({ id: "IDS_CANCEL" }),
            () => this.deletePatient("Patient", this.props.Login.masterData.SelectedPatient, "delete", deleteId));
    }

    closeModal = () => {
        let loadEsign = this.props.Login.loadEsign;
        let openModal = this.props.Login.openModal;
        let selectedRecord = this.props.Login.selectedRecord;

        if (this.props.Login.loadEsign) {
            if (this.props.Login.operation === "delete") {
                loadEsign = false;
                openModal = false;
                selectedRecord = {};
            }
            else {
                loadEsign = false;
            }
        }
        else {
            openModal = false;
            selectedRecord = {};
        }

        const updateInfo = {
            typeName: DEFAULT_RETURN,
            data: { openModal, loadEsign, selectedRecord, selectedId: null }
        }
        this.props.updateStore(updateInfo);

    }

    onComboChange = (comboData, fieldName) => {
        const selectedRecord = this.state.selectedRecord || {};
        selectedRecord[fieldName] = comboData;
        this.setState({ selectedRecord });
    }

    onNumericInputOnChange = (value, name) => {
        const selectedRecord = this.state.selectedRecord || {};
        selectedRecord[name] = value;
        this.setState({ selectedRecord });
    }

    onInputOnChange = (event) => {
        const selectedRecord = this.state.selectedRecord || {};

        if (event.target.type === 'checkbox') {
            selectedRecord[event.target.name] = event.target.checked === true ? transactionStatus.YES : transactionStatus.NO;

        }
        else {
            if (event.target.name === "sphoneno") {
                if (event.target.value !== "") {
                    event.target.value = validatePhoneNumber(event.target.value);
                    selectedRecord[event.target.name] = event.target.value !== "" ? event.target.value : selectedRecord[event.target.name];
                }
                else {
                    selectedRecord[event.target.name] = event.target.value;
                }
            }
            else {
                selectedRecord[event.target.name] = event.target.value;
            }
        }
        this.setState({ selectedRecord });
    }

    handleDateChange = (dateName, dateValue) => {
        const { selectedRecord } = this.state;
        selectedRecord[dateName] = dateValue;
        const age = ageCalculate(dateValue);
        selectedRecord["sage"] = age;

        this.setState({ selectedRecord });
    }

    onSaveClick = (saveType, formRef) => {

        let patientData = [];
        patientData["userinfo"] = this.props.Login.userInfo;

        let postParam = { inputListName: "PatientList", selectedObject: "SelectedPatient", primaryKeyField: "npatientcode" };

        if (this.props.Login.operation === "update") {
            postParam['primaryKeyValue'] = this.props.Login.masterData.SelectedPatient.npatientcode;
            patientData["patient"] = {"npatientcode": this.props.Login.masterData.SelectedPatient.npatientcode,
                           "sfirstname": this.state.selectedRecord.sfirstname,
                           "slastname": this.state.selectedRecord.slastname ,
                           "snationalid": this.state.selectedRecord.snationalid, 
                           "sage": this.state.selectedRecord.sage,
                           "sphoneno": this.state.selectedRecord.sphoneno ,
                           "saddress":this.state.selectedRecord.saddress ,
                           "sfathername":this.state.selectedRecord.sfathername
                        //    "ddob": formatInputDate(
                        //     this.state.selectedRecord["ddob"],false)        
                            }
        }
        else {
            patientData["patient"] = { "nsitecode": this.props.Login.userInfo.nmastersitecode };

            this.patientFieldList.map(item => {
                return patientData["patient"][item] = this.state.selectedRecord[item]
            });
        }

        patientData["patient"]["ngendercode"] = this.state.selectedRecord["ngendercode"] ? this.state.selectedRecord["ngendercode"].value : transactionStatus.NA;


        // if (patientData["patient"]["ddob"] !== undefined
        //     && patientData["patient"]["ddob"] !== null && patientData["patient"]["ddob"] !== "") {
        //     patientData["patient"]["ddob"] = formatInputDate(patientData["patient"]["ddob"],false);

        // }
        if (this.state.selectedRecord["ddob"]) {
            patientData["patient"]["ddob"] = formatInputDate(
              this.state.selectedRecord["ddob"],
              false
            );
          }
        const inputParam = {
            classUrl: "patient",
            methodUrl: "Patient",
            inputData: patientData,
            operation: this.props.Login.operation,
            saveType, formRef, postParam, searchRef: this.searchRef,
            isClearSearch: this.props.Login.isClearSearch
        }
        const masterData = this.props.Login.masterData;

        if (showEsign(this.props.Login.userRoleControlRights, this.props.Login.userInfo.nformcode, this.props.Login.ncontrolCode)) {

            const updateInfo = {
                typeName: DEFAULT_RETURN,
                data: {
                    loadEsign: true, screenData: { inputParam, masterData }, saveType
                }
            }
            this.props.updateStore(updateInfo);
        }
        else {
            this.props.crudMaster(inputParam, masterData, "openModal");
        }

    }

    deletePatient = (methodUrl, selectedPatient, operation, ncontrolCode) => {

        const postParam = {
            inputListName: "PatientList", selectedObject: "SelectedPatient",
            primaryKeyField: "npatientcode",
            primaryKeyValue: selectedPatient.npatientcode,
            fetchUrl: "patient/getPatient",
            fecthInputObject: { userinfo: this.props.Login.userInfo },
        }

        const inputParam = {
            classUrl: this.props.Login.inputParam.classUrl,
            methodUrl, postParam,
            inputData: {
                "userinfo": this.props.Login.userInfo,
                "patient": selectedPatient
            },
            operation,
            isClearSearch: this.props.Login.isClearSearch
        }

        const masterData = this.props.Login.masterData;

        if (showEsign(this.props.Login.userRoleControlRights, this.props.Login.userInfo.nformcode, ncontrolCode)) {
            const updateInfo = {
                typeName: DEFAULT_RETURN,
                data: {
                    loadEsign: true, screenData: { inputParam, masterData },
                    openModal: true, screenName: "IDS_PATIENTMASTER", operation
                }
            }
            this.props.updateStore(updateInfo);
        }
        else {
            this.props.crudMaster(inputParam, masterData, "openModal");
        }

    }

    validateEsign = () => {
        const inputParam = {
            inputData: {
                "userinfo": {
                    ...this.props.Login.userInfo,
                    sreason: this.state.selectedRecord["esigncomments"]
                },
                password: this.state.selectedRecord["esignpassword"]
            },
            screenData: this.props.Login.screenData
        }
        this.props.validateEsignCredential(inputParam, "openModal");
    }

    handleExpandChange = (row, dataState) => {
        const viewParam = {
            userInfo: this.props.Login.userInfo, primaryKeyField: "npreregno",
            masterData: this.props.Login.masterData
        };

        this.props.getTestParameter({
            ...viewParam, dataState,
            primaryKeyValue: row["dataItem"][viewParam.primaryKeyField],
            viewRow: row["dataItem"]
        });

    }

    reloadData = () => {
        this.searchRef.current.value = "";

        const inputParam = {
            inputData: { "userinfo": this.props.Login.userInfo },
            classUrl: "patient",
            methodUrl: "Patient",
            displayName: "IDS_PATIENTMASTER",
            userInfo: this.props.Login.userInfo,
            isClearSearch: this.props.Login.isClearSearch

        };

        this.props.callService(inputParam);
    }
}
export default connect(mapStateToProps, {
    callService, crudMaster, validateEsignCredential,
    updateStore, getPatientDetail, getPatientComboService,
    filterColumnData, getTestParameter, getPatientReport
})(injectIntl(PatientMaster));
