export function getReactInputFields(userinfo, operation, respObj) {
  return function (dispatch) {
    dispatch(initRequest(true));
    const listURL = [];
    listURL[0] = rsapi.post("dynamicpreregdesign/getReactComponents", {
      userinfo
    });
    listURL[1] = rsapi.post("dynamicpreregdesign/getReactInputFields", {
      userinfo
    });
    listURL[2] = rsapi.post("registrationsubtype/getPeriods", {
      userinfo
    });
    listURL[3] = rsapi.post("dynamicpreregdesign/getReactStaticFilterTables", {
      userinfo
    });
    Axios.all(listURL).then(response => {
      respObj = respObj ? { ...respObj
      } : {};
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          ReactInputFields: response[1].data,
          ReactComponents: response[0].data.components,
          Period: constructOptionList(response[2].data, 'nperiodcode', 'speriodname').get("OptionList"),
          ReactTables: constructOptionList(response[0].data.tables, 'nquerybuildertablecode', 'sdisplayname').get("OptionList"),
          staticfiltertables: constructOptionList(response[3].data, 'nquerybuilderstaticfiltercode', 'displayname').get("OptionList"),
          selectedFieldRecord: {},
          openModal: false,
          loading: false,
          openPortal: true,
          operation,
          showFilter: false,
          ...respObj
        }
      });
    }).catch(error => {
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          loading: false
        }
      });

      if (error.response.status === 500) {
        toast.error(error.message);
      } else {
        toast.warn(error.response.data);
      }
    });
  };
}
