                        Map["nflag"] = 2;
                        Map["ntype"] = 3;
                        Map["activeTestTab"] = this.props.Login.activeTestTab || "IDS_PARAMETERRESULTS";
                        Map["activeSampleTab"] = this.props.Login.activeSampleTab || "IDS_SAMPLEATTACHMENTS";
