                    this.props.preregRecordToQuarantine(inputParam, this.props.Login.masterData)
                }
            } else {
                toast.warn(this.props.intl.formatMessage({ id: "IDS_SELECTPREREGISTERSAMPLES" }))
            }
        } else {
            toast.warn(this.props.intl.formatMessage({ id: "IDS_SELECTPREREGISTERSAMPLES" }))
        }

