      let {
        Specification,
        AgaramTree,
        ActiveKey,
        FocusKey,
        OpenNodes,
        Manufacturer
      } = [];
      const selectedSpec = {};

      if (response.data["rtn"] === true) {
        Specification = constructOptionList(response.data["Specification"] || [], "nallottedspeccode", "sspecname", undefined, undefined, true).get("OptionList");
        AgaramTree = response.data["AgaramTree"];
        ActiveKey = response.data["ActiveKey"];
