export function getTest(objComponent, LoginProps, subSample, specBasedComponent) {
  return function (dispatch) {
    if (Object.keys(objComponent).length > 0) {
      dispatch(initRequest(true));
      rsapi.post("/registration/getTestfromDB", {
        nspecsampletypecode: objComponent.nspecsampletypecode,
        slno: objComponent.slno,
        subSample: subSample,
        nallottedspeccode: objComponent.nallottedspeccode,
        specBasedComponent: specBasedComponent
      }).then(response => {
        let TestData = response.data;
        let Test = LoginProps.Test || [];
        let componentTest = Test[objComponent.slno] ? Test[objComponent.slno] : [];
        const TestCombined = filterRecordBasedOnTwoArrays(TestData, componentTest, "ntestgrouptestcode"); // const lstComponentMap = constructOptionList(TestCombined|| [], "ntestgrouptestcode",
        //"stestsynonym", undefined, undefined, true);

        dispatch({
          type: DEFAULT_RETURN,
          payload: {
            TestCombined,
            loadTest: true,
            openChildModal: false,
            ChildscreenName: "Test",
            childoperation: "create",
            parentPopUpSize: "lg",
            loading: false
          }
        });
      }).catch(error => {
        toast.error(error.message);
        dispatch({
          type: DEFAULT_RETURN,
          payload: {
            loading: false
          }
        });
      });
    } else {
      toast.warn(intl.formatMessage({
        id: "IDS_SELECTCOMPONENTTOADDTEST"
      }));
    }
  };
}
export function addsubSample(userinfo, specBasedComponent, Map) {
  return function (dispatch) {
    dispatch(initRequest(true));
    let urlArray = [];
    const Unit = rsapi.post("/unit/getUnit", {
      userinfo
    });
    let Componnet = {};

    if (specBasedComponent === 4) {
      Componnet = rsapi.post("/component/getComponent", {
        userinfo
      });
    } else {
      Componnet = rsapi.post("/registration/getComponentBySpec", Map);
    }

    urlArray = [Unit, Componnet];
    Axios.all(urlArray).then(response => {
      const lstUnitMap = constructOptionList(response[0].data || [], "nunitcode", "sunitname", undefined, undefined, true);
      let comp = [];

      if (specBasedComponent === 4) {
        comp = response[1].data;
      } else {
        comp = response[1].data.lstComponent;
      }

      const lstComponentMap = constructOptionList(comp || [], "ncomponentcode", "scomponentname", undefined, undefined, true);
      const lstUnit = lstUnitMap.get("OptionList");
      const lstComponent = lstComponentMap.get("OptionList");
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          ChildscreenName: "SubSample",
          showSaveContinue: true,
          openChildModal: false,
          childoperation: "create",
          parentPopUpSize: "lg",
          loading: false,
          lstComponent,
          openPortal: true,
          openModal: false,
          Unit: lstUnit,
          operation: "create",
          loadSubSample: true
        }
      });
    }).catch(error => {
      toast.error(error.message);
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          loading: false
        }
      });
    });
  };
}
