    componentDidUpdate(previousProps) {
        if (this.props.Login.masterData !== previousProps.Login.masterData) {
            if (this.props.Login.userInfo.nformcode !== previousProps.Login.userInfo.nformcode) {
                const userRoleControlRights = [];
                if (this.props.Login.userRoleControlRights) {
                    this.props.Login.userRoleControlRights[this.props.Login.userInfo.nformcode] && Object.values(this.props.Login.userRoleControlRights[this.props.Login.userInfo.nformcode]).map(item =>
                        userRoleControlRights.push(item.ncontrolcode))
                }
                const controlMap = getControlMap(this.props.Login.userRoleControlRights, this.props.Login.userInfo.nformcode)

                const multilingualmastersMap = constructOptionList(this.props.Login.masterData.multilingualmasters || [], "nmultilingualmasterscode",
                    "sdisplayname", undefined, undefined, true);
                const multilingualmasters = multilingualmastersMap.get("OptionList");
                this.setState({
                    userRoleControlRights, controlMap, multilingualmasters: multilingualmasters,data: this.props.Login.masterData,
                    dataResult: process(this.props.Login.masterData.listofItem ? this.props.Login.masterData.listofItem : [], this.state.dataState)
                });
            }
            else {
                let { dataState } = this.state;
                if (this.props.Login.dataState === undefined) {
                    dataState = { skip: 0, take: this.props.Login.settings ? parseInt(this.props.Login.settings[14]) : 5 }
                }
                const FilterQueryMap = constructOptionList(this.props.Login.masterData.QueryBuilderViewsName || [], "nquerybuilderviewscode",
                    "sdisplayname", undefined, undefined, true);
                const QueryBuilderViewsName = FilterQueryMap.get("OptionList");
                this.setState({
                    data: this.props.Login.masterData, QueryBuilderViewsName:QueryBuilderViewsName,
                    dataResult: process(this.props.Login.masterData.listofItem ? this.props.Login.masterData.listofItem : [], dataState),
                    dataState
                });
            }
        }
        else if (this.props.Login.selectedRecord !== previousProps.Login.selectedRecord) {
            this.setState({ selectedRecord: this.props.Login.selectedRecord });
        }
    }

    onInputOnChange = (event, name, item) => {
        const selectedRecord = this.state.selectedRecord || {};
        if (event.target.type === 'checkbox') {
            selectedRecord[event.target.name] = event.target.checked === true ? 3 : 4;
        }
        else {
            if (name == 'English') {
                selectedRecord['sdisplayname'] = { ...selectedRecord['sdisplayname'], [event.target.name]: event.target.value }

                {
                    if (item == 'sactiondisplaystatus') {
                        selectedRecord['sactiondisplaystatus'] = { ...selectedRecord['sactiondisplaystatus'], [event.target.name]: event.target.value };
                    } else if (item == 'salertdisplaystatus') {
                        selectedRecord['salertdisplaystatus'] = { ...selectedRecord['salertdisplaystatus'], [event.target.name]: event.target.value };
                    } else {
                        selectedRecord['stransdisplaystatus'] = { ...selectedRecord['stransdisplaystatus'], [event.target.name]: event.target.value };
                    }
                }
                selectedRecord['scontrolids'] = { ...selectedRecord['scontrolids'], [event.target.name]: event.target.value };
                selectedRecord['approvalsubtypename'] = { ...selectedRecord['approvalsubtypename'], [event.target.name]: event.target.value };
                selectedRecord['sampletypename'] = { ...selectedRecord['sampletypename'], [event.target.name]: event.target.value };
                selectedRecord['stemplatetypename'] = { ...selectedRecord['stemplatetypename'], [event.target.name]: event.target.value };
                selectedRecord['speriodname'] = { ...selectedRecord['speriodname'], [event.target.name]: event.target.value };
                selectedRecord['sgendername'] = { ...selectedRecord['sgendername'], [event.target.name]: event.target.value };
                selectedRecord['sschedulertypename'] = { ...selectedRecord['sschedulertypename'], [event.target.name]: event.target.value };
                selectedRecord['tablename'] = { ...selectedRecord['tablename'], [event.target.name]: event.target.value };
                selectedRecord['displayname'] = { ...selectedRecord['displayname'], [event.target.name]: event.target.value };
                selectedRecord['sdefaultname'] = event.target.value == "" ? undefined : event.target.value
            }
            else {
                selectedRecord['sdisplayname'] = { ...selectedRecord['sdisplayname'], [event.target.name]: event.target.value }
                selectedRecord['scontrolids'] = { ...selectedRecord['scontrolids'], [event.target.name]: event.target.value };
                selectedRecord['approvalsubtypename'] = { ...selectedRecord['approvalsubtypename'], [event.target.name]: event.target.value };
                selectedRecord['sampletypename'] = { ...selectedRecord['sampletypename'], [event.target.name]: event.target.value };
                selectedRecord['stemplatetypename'] = { ...selectedRecord['stemplatetypename'], [event.target.name]: event.target.value };
                selectedRecord['speriodname'] = { ...selectedRecord['speriodname'], [event.target.name]: event.target.value };
                selectedRecord['sgendername'] = { ...selectedRecord['sgendername'], [event.target.name]: event.target.value };
                selectedRecord['sschedulertypename'] = { ...selectedRecord['sschedulertypename'], [event.target.name]: event.target.value };
                selectedRecord['tablename'] = { ...selectedRecord['tablename'], [event.target.name]: event.target.value };
                selectedRecord['displayname'] = { ...selectedRecord['displayname'], [event.target.name]: event.target.value };
                if (item == "sactiondisplaystatus") {
                    selectedRecord['sactiondisplaystatus'] = { ...selectedRecord['sactiondisplaystatus'], [event.target.name]: event.target.value };
                } else if (item == "salertdisplaystatus") {
                    selectedRecord['salertdisplaystatus'] = { ...selectedRecord['salertdisplaystatus'], [event.target.name]: event.target.value }
                } else {
                    selectedRecord['stransdisplaystatus'] = { ...selectedRecord['stransdisplaystatus'], [event.target.name]: event.target.value }
                }
                selectedRecord[event.target.name] = event.target.value;
            }
        }
        this.setState({ selectedRecord });
    }

    onComboChange = (comboData, fieldName) => {
        const selectedRecord = this.state.selectedRecord || {};
        selectedRecord[fieldName] = comboData;
        if(this.props.Login.masterData.headername == "QueryBuilderViewsColumns"||this.props.Login.masterData.headername == "testmasterview") {
        if (fieldName === "nmultilingualmasterscode") {
            this.props.comboService({
                inputData: {
                    userinfo: this.props.Login.userInfo,
                    sdisplayname: selectedRecord.nmultilingualmasterscode.label,
                    primarykey: selectedRecord.nmultilingualmasterscode.value
                },
                masterData: this.props.Login.masterData,
                userinfo: this.props.Login.userInfo,  
                 sdisplayname: selectedRecord.nmultilingualmasterscode.label,
                
            });
        }else{
            this.props.comboService({
                inputData: {
                    userinfo: this.props.Login.userInfo,
                    sdisplayname: selectedRecord.nquerybuilderviewscode.label,
                    sviewname:selectedRecord.nquerybuilderviewscode.item.sviewname,
                    primarykey: selectedRecord.nquerybuilderviewscode.value
                },
                masterData: this.props.Login.masterData,
                userinfo: this.props.Login.userInfo,  
                
            });
        }
    }else{
        if (fieldName === "nmultilingualmasterscode") {
            this.props.comboService({
                inputData: {
                    userinfo: this.props.Login.userInfo,
                    sdisplayname: selectedRecord.nmultilingualmasterscode.label,
                    primarykey: selectedRecord.nmultilingualmasterscode.value
                },
                masterData: this.props.Login.masterData,
                userinfo: this.props.Login.userInfo,  
                 sdisplayname: selectedRecord.nmultilingualmasterscode.label,
                
            });
        }
    }
