import React, { Component } from 'react';
import { Row, Col, Card, Nav, FormGroup, FormLabel, InputGroup, FormControl } from 'react-bootstrap';
import { connect } from 'react-redux';
import { toast } from 'react-toastify';
import { FormattedMessage } from 'react-intl';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPencilAlt, faTrashAlt } from '@fortawesome/free-solid-svg-icons';
import {callService, crudMaster, updateStore, getSiteDetail,getSiteCombo,
        validateEsignCredential,filterColumnData} from '../../actions';

import { injectIntl } from 'react-intl';
import ListMaster from '../../components/list-master/list-master.component';
import { transactionStatus } from '../../components/Enumeration';
import { showEsign,validatePhoneNumber, getControlMap ,validateEmail} from '../../components/CommonScript';
import { ReadOnlyText, ContentPanel } from '../../components/App.styles';
import ReactTooltip from 'react-tooltip';
import SlideOutModal from '../../components/slide-out-modal/SlideOutModal';
import AddSite from '../../pages/basemaster/AddSite';
import Esign from '../audittrail/Esign';
import { DEFAULT_RETURN } from '../../actions/LoginTypes';
import ConfirmMessage from '../../components/confirm-alert/confirm-message.component';


const mapStateToProps = state => {
    return ({ Login: state.Login })
}


class Site extends Component {

    constructor(props) {
        super(props);
        const sectionDataState = { skip: 0, take: 10 };
        this.state = ({
            selectedRecord: {},
            error: "",
            userRoleControlRights: [],
            selectedSite: undefined,
            controlMap: new Map(),
            Site: [],
            sectionDataState
        });
        this.searchRef = React.createRef();
        this.searchFieldList = ["ssitename", "ssiteaddress","scontactperson", "sphoneno", "semail", "sfaxno"]
        this.confirmMessage = new ConfirmMessage();
      
    }
 
    componentDidUpdate(previousProps) {
        let updateState = false;
        let { controlMap, selectedRecord, userRoleControlRights,dataState } = this.state;
        if (this.props.Login.masterData !== previousProps.Login.masterData) {

            if (this.props.Login.userInfo.nformcode !== previousProps.Login.userInfo.nformcode) {
                userRoleControlRights = [];
                if (this.props.Login.userRoleControlRights) {
                    this.props.Login.userRoleControlRights[this.props.Login.userInfo.nformcode] && Object.values(this.props.Login.userRoleControlRights[this.props.Login.userInfo.nformcode]).map(item =>
                        userRoleControlRights.push(item.ncontrolcode))
                }
                controlMap = getControlMap(this.props.Login.userRoleControlRights, this.props.Login.userInfo.nformcode)

                
            }
            dataState = {skip:0,take:this.props.Login.settings ? parseInt(this.props.Login.settings[14]) : 5}
            updateState = true;
        }
        else if (this.props.Login.selectedRecord !== this.state.selectedRecord) {

            selectedRecord = this.props.Login.selectedRecord;
            updateState = true;

        }

      if (updateState) {
            this.setState({
                userRoleControlRights, controlMap, selectedRecord,dataState
            });
        }
    }   

    validateEsign = () => {
        const inputParam = {
            inputData: {
                "userinfo": {
                    ...this.props.Login.userInfo,
                    sreason: this.state.selectedRecord["esigncomments"]
                },
                password: this.state.selectedRecord["esignpassword"]
            },
            screenData: this.props.Login.screenData
        }
        this.props.validateEsignCredential(inputParam, "openModal");
    }

   closeModal = () => {
    let loadEsign = this.props.Login.loadEsign;
    let openModal = this.props.Login.openModal;
    let selectedRecord = this.props.Login.selectedRecord;
    if (this.props.Login.loadEsign) {
        if (this.props.Login.operation === "delete") {
            loadEsign = false;
            openModal = false;
            selectedRecord = {};
        }
        else {
            loadEsign = false;
        }
    }
    else {
        openModal = false;
        selectedRecord = {};
    }

    const updateInfo = {
        typeName: DEFAULT_RETURN,
        data: { openModal, loadEsign, selectedRecord, selectedId: null }
    }
    this.props.updateStore(updateInfo);

}
static getDerivedStateFromProps(props, state) {

    if (props.Login.masterStatus !== "" && props.Login.masterStatus !== state.masterStatus) {
        toast.warn(props.Login.masterStatus);
        props.Login.masterStatus = "";
    }

    if (props.Login.error !== state.error) {
        toast.error(props.Login.error)
        props.Login.error = "";
    }
    if (props.Login.selectedRecord === undefined) {
        return { selectedRecord: {} }
    }
    return null;
}
onSaveClick = (saveType, formRef) => {
    //add / edit
    const isValidateEmail = this.state.selectedRecord["semail"]? validateEmail(this.state.selectedRecord["semail"]): true;
 if (isValidateEmail) {  
    let dataState = undefined;
    let operation = "";
    let inputData = [];
    let selectedId = null;
    inputData["userinfo"] = this.props.Login.userInfo;
    let postParam = {
        selectedObject: "selectedSite",
        primaryKeyField: "nsitecode",
        inputListName: "Site",
        fecthInputObject: { userinfo: this.props.Login.userInfo }
    }
    if (this.props.Login.operation === "update") {
        postParam['primaryKeyValue'] = this.props.Login.masterData.selectedSite.nsitecode;
        inputData["site"] = { "nsitecode": this.props.Login.masterData.selectedSite.nsitecode,
                           "nmastersitecode": this.props.Login.userInfo.nmastersitecode,
                           "scontactperson": this.state.selectedRecord.scontactperson!==null?this.state.selectedRecord.scontactperson:"",
                           "ssiteaddress": this.state.selectedRecord.ssiteaddress ,
                           "sphoneno": this.state.selectedRecord.sphoneno, 
                           "ssitename": this.state.selectedRecord.ssitename,
                           "sfaxno": this.state.selectedRecord.sfaxno ,
                           "semail":this.state.selectedRecord.semail!==null?this.state.selectedRecord.semail:"" ,
                           "ndefaultstatus":this.state.selectedRecord.ndefaultstatus}


        operation = "update";
        dataState = this.state.dataState;
        selectedId = this.props.Login.selectedId;
    }
    else {

        inputData["site"] = { "nmastersitecode": this.props.Login.userInfo.nmastersitecode,
                            "scontactperson": this.state.selectedRecord.scontactperson!==null?this.state.selectedRecord.scontactperson:"",
                            "ssiteaddress": this.state.selectedRecord.ssiteaddress ,
                            "sphoneno": this.state.selectedRecord.sphoneno,
                            "ssitename": this.state.selectedRecord.ssitename,
                            "sfaxno": this.state.selectedRecord.sfaxno ,
                            "semail": this.state.selectedRecord.semail!==null?this.state.selectedRecord.semail:"" ,
                            "ndefaultstatus":this.state.selectedRecord.ndefaultstatus};


        operation = "create";
    }
    inputData["site"]["ntimezonecode"] = this.state.selectedRecord["ntimezonecode"] ? this.state.selectedRecord["ntimezonecode"].value : "-1";


    const inputParam = {
        classUrl: "site",
        methodUrl: "SiteScreen",
        displayName: this.props.Login.inputParam.displayName,
        inputData: inputData,
        postParam,
        searchRef: this.searchRef,
        operation: operation, saveType, formRef, dataState, selectedId
    }

       const masterData = this.props.Login.masterData;
        if (showEsign(this.props.Login.userRoleControlRights, this.props.Login.userInfo.nformcode, this.props.Login.ncontrolCode)) {
            const updateInfo = {
                typeName: DEFAULT_RETURN,
                data: {
                    loadEsign: true, screenData: { inputParam, masterData }, saveType
                }
            }
            this.props.updateStore(updateInfo);
        }
      else {
        this.props.crudMaster(inputParam, this.props.Login.masterData, "openModal");
    }
}
else {
        toast.info(this.props.intl.formatMessage({ id: "IDS_ENTERVALIDEMAIL" }))
    }
}


onInputOnChange = (event) => {

    const selectedRecord = this.state.selectedRecord || {};
  if (event.target.type === 'checkbox') {
       selectedRecord[event.target.name] = event.target.checked === true ?transactionStatus.YES : transactionStatus.NO;
  }
  else {
    if (event.target.name === "sphoneno") {
        if (event.target.value !== "") {
            event.target.value = validatePhoneNumber(event.target.value);
            selectedRecord[event.target.name] = event.target.value !== "" ? event.target.value : selectedRecord[event.target.name];
        } else {
            selectedRecord[event.target.name] = event.target.value
        }
    }

    else {
        selectedRecord[event.target.name] = event.target.value;
    }
  }
    this.setState({ selectedRecord });
}

DeleteSite = (operation, ncontrolCode) => {
    let inputData = [];

        inputData["site"] = this.props.Login.masterData.selectedSite;
        inputData["userinfo"] = this.props.Login.userInfo;

        const postParam = {
            inputListName: "Site", selectedObject: "selectedSite",
            primaryKeyField: "nsitecode",
            primaryKeyValue: this.props.Login.masterData.selectedSite.nsitecode,
            fecthInputObject: { userinfo: this.props.Login.userInfo },
            fetchUrl:"site/getSiteScreen"
        }

        const inputParam = {
            methodUrl: "SiteScreen", postParam,
            classUrl: "site",
            inputData: inputData,
            operation: "delete"
        }
        const masterData = this.props.Login.masterData;
        if (showEsign(this.props.Login.userRoleControlRights, this.props.Login.userInfo.nformcode, ncontrolCode)) {
            const updateInfo = {
                typeName: DEFAULT_RETURN,
                data: {
                    loadEsign: true, screenData: { inputParam, masterData },
                    openModal: true, screenName: "Site", operation
                }
            }
            this.props.updateStore(updateInfo);
        }
        else {
            this.props.crudMaster(inputParam, masterData, "openModal");
        }


}


ConfirmDelete = (deleteId) => {
    this.confirmMessage.confirm("deleteMessage", this.props.intl.formatMessage({ id: "IDS_DELETE" }), this.props.intl.formatMessage({ id: "IDS_DEFAULTCONFIRMMSG" }),
        this.props.intl.formatMessage({ id: "IDS_OK" }), this.props.intl.formatMessage({ id: "IDS_CANCEL" }),
        () => this.DeleteSite("delete", deleteId, "IDS_SITE"));
}

reloadData = () => {
    this.searchRef.current.value = "";
    const inputParam = {
        inputData: { "userinfo": this.props.Login.userInfo },
        classUrl: "site",
        methodUrl: "SiteScreen",
        displayName: "IDS_SITE",
        userInfo: this.props.Login.userInfo
    };

    this.props.callService(inputParam);
}

onComboChange = (comboData, fieldName) => {
    if (comboData !== null) {
        const selectedRecord = this.state.selectedRecord || {};
        selectedRecord[fieldName] = comboData;

        this.setState({ selectedRecord });
    }

}
render(){
    let mandatoryFields = [];
            mandatoryFields.push(
                { "mandatory": true, "idsName": "IDS_SITENAME", "dataField": "ssitename"  , "mandatoryLabel":"IDS_ENTER", "controlType": "textbox"},
                { "mandatory": true, "idsName": "IDS_SITEADDRESS", "dataField": "ssiteaddress"  , "mandatoryLabel":"IDS_ENTER", "controlType": "textbox"},
                { "mandatory": true, "idsName": "IDS_PHONENO", "dataField": "sphoneno" , "mandatoryLabel":"IDS_ENTER", "controlType": "textbox" },
                { "mandatory": true, "idsName": "IDS_FAXNO", "dataField": "sfaxno" , "mandatoryLabel":"IDS_ENTER", "controlType": "textbox" },
               
            )
    const addId = this.state.controlMap.has("AddSite") && this.state.controlMap.get("AddSite").ncontrolcode;
    const editId = this.state.controlMap.has("EditSite") && this.state.controlMap.get("EditSite").ncontrolcode;
    const deleteId = this.state.controlMap.has("DeleteSite") && this.state.controlMap.get("DeleteSite").ncontrolcode;
    
    const filterParam = {
        inputListName: "Site", selectedObject: "selectedSite", primaryKeyField: "nsitecode",
        fetchUrl: "site/getSiteScreen", fecthInputObject: { userinfo: this.props.Login.userInfo }, masterData: this.props.Login.masterData,
        searchFieldList: this.searchFieldList
    };
    const { selectedSite } = this.props.Login.masterData;
    return(
        
         <>
                         <ReactTooltip place="bottom" globalEventOff='click' id="tooltip_list_wrap" />
                         <div className="client-listing-wrap mtop-fixed-breadcrumb fixed_breadcrumd">
        <Row noGutters={true}>
        <Col md={4}>
                            <ListMaster
                                formatMessage={this.props.intl.formatMessage}
                                screenName={"Site"}
                                masterData={this.props.Login.masterData}
                                userInfo={this.props.Login.userInfo}
                                masterList={this.props.Login.masterData.searchedData || this.props.Login.masterData.Site}
                                getMasterDetail={(Site) => this.props.getSiteDetail(Site, this.props.Login.userInfo, this.props.Login.masterData)}
                                selectedMaster={this.props.Login.masterData.selectedSite}
                                primaryKeyField="nsitecode"
                                mainField="ssitename"
                                firstField="scontactperson"
                                secondField="stransdisplaystatus"

                                filterColumnData={this.props.filterColumnData}
                                filterParam={filterParam}
                                userRoleControlRights={this.state.userRoleControlRights}
                                searchRef={this.searchRef}
                                addId={addId}
                                hidePaging={false}

                                openModal={() => this.props.getSiteCombo("IDS_SITE", "create", "nsitecode", this.props.Login.masterData, this.props.Login.userInfo, addId)}
                                reloadData={this.reloadData}

                            />

                        </Col>
                        <Col md={8}>
                            <Row>
                                <Col md={12}>
                                    <ContentPanel className="panel-main-content">
                                        <Card className="border-0">
                                            {this.props.Login.masterData.selectedSite ?
                                                <>
                                                    <Card.Header>
                                                       
                                                        <Card.Title className="product-title-main">
                                                            {this.props.Login.masterData.selectedSite.ssitename}
                                                        </Card.Title>
                                                        <Card.Subtitle>
                                                            <div className="d-flex product-category">
                                                                <h2 className="product-title-sub flex-grow-1">

                                                                    
                                                                </h2>
                                                                <div className="d-inline">
                                                                    <Nav.Link name="editSite"
                                                                        data-tip={this.props.intl.formatMessage({ id: "IDS_EDIT" })}
                                                                        data-for="tooltip_list_wrap"
                                                                        hidden={this.state.userRoleControlRights.indexOf(editId) === -1}
                                                                        className="btn btn-circle outline-grey mr-2"
                                                                        onClick={(e) => this.props.getSiteCombo("IDS_SITE", "update", "nsitecode", this.props.Login.masterData, this.props.Login.userInfo, editId)}
                                                                    >
                                                                        <FontAwesomeIcon icon={faPencilAlt} />
                                                                    </Nav.Link>
                                                                    <Nav.Link name="deleteSite" className="btn btn-circle outline-grey mr-2 action-icons-wrap"
                                                                        data-tip={this.props.intl.formatMessage({ id: "IDS_DELETE" })}
                                                                        data-for="tooltip_list_wrap"
                                                                        hidden={this.state.userRoleControlRights.indexOf(deleteId) === -1}
                                                                        onClick={() => this.ConfirmDelete(deleteId)}>
                                                                        <FontAwesomeIcon icon={faTrashAlt} />
                                                                        
                                                                    </Nav.Link>

                                                                </div>
                                                            </div>

                                                        </Card.Subtitle>
                                                    </Card.Header>
                                                    <Card.Body>
                                                        <Row>
                                                            <Col md={4}>
                                                                <FormGroup>
                                                                    <FormLabel><FormattedMessage id="IDS_SITENAME" message="Sitename" /></FormLabel>
                                                                    <ReadOnlyText>{this.props.Login.masterData.selectedSite.ssitename}</ReadOnlyText>
                                                                </FormGroup>
                                                            </Col>
                                                            <Col md={4}>
                                                                <FormGroup>
                                                                    <FormLabel><FormattedMessage id="IDS_SITEADDRESS" message="Site Address" /></FormLabel>
                                                                    <ReadOnlyText>{this.props.Login.masterData.selectedSite.ssiteaddress}</ReadOnlyText>
                                                                </FormGroup>
                                                            </Col>

                                                            <Col md={4}>
                                                                <FormGroup>
                                                                    <FormLabel><FormattedMessage id="IDS_CONTACTPERSON" message="Contact Person" /></FormLabel>
                                                                    <ReadOnlyText>{this.props.Login.masterData.selectedSite.scontactperson === null || this.props.Login.masterData.selectedSite.scontactperson.length === 0 ? '-' :
                                                                    this.props.Login.masterData.selectedSite.scontactperson}</ReadOnlyText>
                                                                </FormGroup>
                                                            </Col>

                                                            <Col md={4}>
                                                                <FormGroup>
                                                                    <FormLabel><FormattedMessage id="IDS_PHONENO" message="Phone Number" /></FormLabel>
                                                                    <ReadOnlyText>{this.props.Login.masterData.selectedSite.sphoneno}</ReadOnlyText>
                                                                </FormGroup>
                                                            </Col>

                                                            <Col md={4}>
                                                                <FormGroup>
                                                                    <FormLabel><FormattedMessage id="IDS_FAXNO" message="Fax No" /></FormLabel>
                                                                    <ReadOnlyText>{this.props.Login.masterData.selectedSite.sfaxno}</ReadOnlyText>
                                                                </FormGroup>
                                                            </Col>

                                                            <Col md={4}>
                                                                <FormGroup>
                                                                    <FormLabel><FormattedMessage id="IDS_EMAIL" message="Email" /></FormLabel>
                                                                    <ReadOnlyText>
                                                                        {this.props.Login.masterData.selectedSite.semail === null || this.props.Login.masterData.selectedSite.semail.length === 0 ? '-' :
                                                                            this.props.Login.masterData.selectedSite.semail}
                                                                    </ReadOnlyText>
                                                                </FormGroup>
                                                            </Col>

                                                            <Col md={4}>
                                                                <FormGroup>
                                                                    <FormLabel><FormattedMessage id="IDS_TIMEZONE" message="Time" /></FormLabel>
                                                                    <ReadOnlyText>
                                                                        {this.props.Login.masterData.selectedSite.ntimezonecode === null || this.props.Login.masterData.selectedSite.ntimezonecode.length === 0 ? '-' :
                                                                            this.props.Login.masterData.selectedSite.stimezoneid}
                                                                    </ReadOnlyText>
                                                                </FormGroup>
                                                            </Col> 
                                                                                    
                                                                                                                      
                                                        </Row>
                                                    
                                                    </Card.Body>

                                                </>
                                                : ""
                                            }
                                        </Card>
                                    </ContentPanel>
                                </Col></Row>
                        </Col>
                    </Row>
                        </div>
                        {this.props.Login.openModal ? 
                    <SlideOutModal show={true}
                        closeModal={this.closeModal}
                        operation={this.props.Login.operation}
                        inputParam={this.props.Login.inputParam}
                        screenName={'IDS_SITE'}
                        esign={this.props.Login.loadEsign}
                        validateEsign={this.validateEsign}
                        mandatoryFields={mandatoryFields}

                        masterStatus={this.props.Login.masterStatus}
                        updateStore={this.props.updateStore}
                        selectedRecord={this.state.selectedRecord || {}}
                        onSaveClick={this.onSaveClick}

                        addComponent={this.props.Login.loadEsign ?
                            <Esign operation={this.props.Login.operation}
                                onInputOnChange={this.onInputOnChange}
                                inputParam={this.props.Login.inputParam}
                                selectedRecord={this.state.selectedRecord || {}}
                            />
                            : <AddSite
                                selectedRecord={this.state.selectedRecord || {}}
                                onInputOnChange={this.onInputOnChange}
                                TimeZoneList={this.props.Login.TimeZoneList}
                                onComboChange={this.onComboChange}

                            />}
                    />
                    
               :""} 
         </>
    )
}
}

export default connect(mapStateToProps, {filterColumnData, callService,updateStore,
                       getSiteDetail,crudMaster,getSiteCombo, validateEsignCredential 
}
)(injectIntl(Site));
