      Map["RegistrationSample"] = SubSample(this.props.Login.Component ? this.props.Login.Component : [], this.state.specBasedComponent, this.props.Login.masterData.RealRegSubTypeValue ? this.props.Login.masterData.RealRegSubTypeValue.nneedsubsample : false, this.state.selectedSpec);
      Map["subsampleDateList"] = this.findSubsampleDateList(this.props.Login.masterData.SubSampleTemplate ? this.props.Login.masterData.SubSampleTemplate.jsondata : this.props.Login.masterData.schedulerSubSampleTemplate.jsondata);
      Map["nregtypecode"] = 1;
      Map["nregsubtypecode"] = 1;
      Map["nsampletypecode"] = 1; // Map["nfilterstatus"] = transactionStatus.PREREGISTER;

      Map["ndesigntemplatemappingcode"] = this.props.Login.masterData.ndesigntemplatemappingcode;
