                                isClearable={false}
                                onChange={(event) => this.props.onFilterComboChange(event, "nprojecttypecode")}
                            />
                        }
                      {this.props.SampleTypeValue && this.props.SampleTypeValue.nsampletypecode === SampleType.PROJECTSAMPLETYPE &&

                        <FormSelectSearch
                            formLabel={this.props.intl.formatMessage({ id: "IDS_PROJECTMASTER" })}
                            placeholder={this.props.intl.formatMessage({ id: "IDS_PROJECTMASTER" })}
                            name="nprojectmastercode"
                            className='form-control'
                            options={this.props.ProjectMaster}
                            value={this.props.ProjectMasterValue ? { "label": this.props.ProjectMasterValue.sprojectcode, "value": this.props.ProjectMasterValue.nprojectmastercode } : ""}
                            optionId="nprojectmastercode"
                            optionValue="sprojectcode"
                            isMandatory={false}
                            isMulti={false}
                            isSearchable={false}
                            isDisabled={false}
