    valiateCheckAll(data) {
        let selectAll = true;
        // let checkRepeatComponent;
        //  let addedComponentList = this.state.addedComponentList || [];
        if (data && data.length > 0) {
            data.forEach(dataItem => {
                if (dataItem.selected) {
                    if (dataItem.selected === false) {
                        selectAll = false;
                    }
                }
                else {
                    selectAll = false;
                }
            })
        }
        else {
            selectAll = false;
        }
        return selectAll;
    }

