                inputParam.masterData.selectedSample.map(sample => {
                    if(sample.npreregno === inputParam.test.npreregno){
                        patientName = sample["Patient First Name"]+" "+sample["Patient Last Name"];
                        patientDOB = sample["Date of birth"].replaceAll("/", "-");
