                    }
                })
                let generateBarcodeValue = inputParam.test.ssamplearno;
                let additionDesignToPrint = patientName+"<br>"+patientDOB+"&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;"+orderIdData;
                this.setState({
