                Map["selectedSample"] = cancelRejectSamplesList;//this.props.Login.masterData.selectedSample
                Map["RegistrationGetSubSample"] = this.props.Login.masterData.RegistrationGetSubSample


                let inputParam = {
                    inputData: Map,
                    postParamList: this.postParamList,
