                    dispatch(getTreeByProduct(mapOfFilterRegData, selectedRecord1, newcomboData.comboData, inputParam));
                  } else {
                    const Material = comboComponents.filter(x => x.name === 'Material');

                    if (Material.length > 0) {
                      const nmaterialcode = selectedRecord1[Material[0].label] && selectedRecord1[Material[0].label].value;
                      mapOfFilterRegData['nproductcode'] = nmaterialcode !== undefined ? nmaterialcode : -1;
