                    withoutCombocomponent,
                    openPortal,
                    columnList,
                    childColumnList,
                    operation,
                    screenName // loadPreregister:true

                  };
                  dispatch(getTreeByProduct(mapOfFilterRegData, selectedRecord1, newcomboData.comboData, inputParam));
                }
              } else {
                mapOfFilterRegData['nproductcode'] = -1;
                mapOfFilterRegData['nproductcatcode'] = -1;
                const inputParam = {
                  timeZoneList,
                  defaultTimeZone,
                  masterData,
                  ncontrolcode: editId,
                  openModal,
                  comboComponents,
                  withoutCombocomponent,
                  openPortal,
                  columnList,
                  childColumnList,
                  operation,
                  screenName //loadPreregister:true

                };
                dispatch(getTreeByProduct(mapOfFilterRegData, selectedRecord1, newcomboData.comboData, inputParam));
              }
            }
          } else {
            mapOfFilterRegData['nproductcode'] = -1;
            mapOfFilterRegData['nproductcatcode'] = -1;
            const inputParam = {
              timeZoneList,
              defaultTimeZone,
              masterData,
              ncontrolcode: editId,
              openModal,
              comboComponents,
              withoutCombocomponent,
              openPortal,
              columnList,
              childColumnList,
              operation,
              screenName // loadPreregister:true

            };
            dispatch(getTreeByProduct(mapOfFilterRegData, selectedRecord1, newcomboData.comboData, inputParam));
          }
        }
      }
    }).catch(error => {
      //console.log("error:", error);
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          loading: false
        }
      });

      if (error.response.status === 500) {
        toast.error(error.message);
      } else {
        toast.warn(error.response.data);
      }
    });
  };
}
export function getChildValues(inputParem, userinfo, selectedRecord, comboData, parentcolumnlist, childcolumnlist, withoutCombocomponent, parentListWithReadonly, productCategory, product, mapOfRegFilterData, instrumentCategory, instrument, materialCategory, material, materialType) {
