            }
          });
        }
      } else if (instrumentCategory) {
        dispatch(getTreeByProduct(mapOfRegFilterData, selectedRecord, { ...returnObj.comboData
        }));
      } else if (materialCategory) {
        dispatch(getTreeByProduct(mapOfRegFilterData, selectedRecord, { ...returnObj.comboData
        }));
      } else if (instrument) {
