          mapOfRegFilterData["nproductcode"] = selectedRecord[inputParem.nameofdefaultcomp === "Instrument" ? inputParem.label : inputParem.InstrumentName] ? selectedRecord[inputParem.nameofdefaultcomp === "Instrument" ? inputParem.label : inputParem.InstrumentName].value : -1;
          dispatch(getTreeByProduct(mapOfRegFilterData, selectedRecord, { ...returnObj.comboData
          }));
        } else {
          selectedRecord["nallottedspeccode"] = "";
          selectedRecord["sversion"] = "";
          dispatch({
