      } else if (material) {
        if (selectedRecord[inputParem.nameofdefaultcomp === "Material" ? inputParem.label : inputParem.MaterialName]) {
          mapOfRegFilterData["nproductcode"] = selectedRecord[inputParem.nameofdefaultcomp === "Material" ? inputParem.label : inputParem.MaterialName] ? selectedRecord[inputParem.nameofdefaultcomp === "Material" ? inputParem.label : inputParem.MaterialName].value : -1;
          dispatch(getTreeByProduct(mapOfRegFilterData, selectedRecord, { ...returnObj.comboData
          }));
        } else {
          selectedRecord["nallottedspeccode"] = "";
          selectedRecord["sversion"] = "";
          dispatch({
            type: DEFAULT_RETURN,
            payload: {
              loading: false,
              comboData: { ...returnObj.comboData
              },
              selectedRecord,
              AgaramTree: [],
              ActiveKey: [],
              FocusKey: [],
              OpenNodes: [],
              Test: [],
              SelectedTest: [],
              selectedSpec: {},
              Component: [],
              selectComponent: {},
              selectedComponent: {},
              Specification: []
            }
          });
        }
      } else if (materialType) {
        if (selectedRecord[inputParem.nameofdefaultcomp === "Material Type" ? inputParem.MaterialCategoryName : inputParem.label]) {
          const productCategory = selectedRecord[inputParem.nameofdefaultcomp === "Material Type" ? inputParem.MaterialCategoryName : inputParem.label] ? selectedRecord[inputParem.nameofdefaultcomp === "Material Type" ? inputParem.MaterialCategoryName : inputParem.label] : undefined; //  mapOfRegFilterData["nproductcatcode"]

          if (productCategory !== undefined) {
            if (productCategory.item.ncategorybasedflow === 3) {
              mapOfRegFilterData["nproductcatcode"] = productCategory.value;
              mapOfRegFilterData["nproductcode"] = -1;
              dispatch(getTreeByProduct(mapOfRegFilterData, selectedRecord, { ...returnObj.comboData
              }));
            } else {
              const product = selectedRecord[inputParem.nameofdefaultcomp === "Material Type" ? inputParem.MaterialName : inputParem.label] ? selectedRecord[inputParem.nameofdefaultcomp === "Material Type" ? inputParem.MaterialName : inputParem.label] : undefined;

              if (product !== undefined) {
                mapOfRegFilterData["nproductcatcode"] = productCategory.value;
                mapOfRegFilterData["nproductcode"] = product.value;
                dispatch(getTreeByProduct(mapOfRegFilterData, selectedRecord, { ...returnObj.comboData
