                      Specification: []
                    }
                  });
                }
              }
            }
          } else {
            selectedRecord["nallottedspeccode"] = "";
            selectedRecord["sversion"] = "";
            dispatch({
              type: DEFAULT_RETURN,
              payload: {
                loading: false,
                comboData: { ...returnObj.comboData
                },
                selectedRecord,
                AgaramTree: [],
                ActiveKey: [],
