                OpenNodes: [],
                Test: [],
                SelectedTest: [],
                selectedSpec: {},
                Component: [],
                selectComponent: {},
                selectedComponent: {},
                Specification: []
              }
            });
          }
        } else {
          selectedRecord["nallottedspeccode"] = "";
          selectedRecord["sversion"] = "";
          dispatch({
            type: DEFAULT_RETURN,
            payload: {
              loading: false,
              comboData: { ...returnObj.comboData
              },
              selectedRecord,
              AgaramTree: [],
