              Test: [],
              SelectedTest: [],
              selectedSpec: {},
              Component: [],
              selectComponent: {},
              selectedComponent: {},
              Specification: []
            }
          });
        }
      } else {
        dispatch({
          type: DEFAULT_RETURN,
