import React from 'react';
import FormSelectSearch from '../../components/form-select-search/form-select-search.component';
//import FormInput from '../../components/form-input/form-input.component';
import { Col, Row } from 'react-bootstrap';
import { injectIntl } from 'react-intl';
//import { CONTAINERTYPE,transactionStatus } from '../../components/Enumeration';
import FormNumericInput from '../../components/form-numeric-input/form-numeric-input.component';



const AddTestSubContractor = (props) => {
 //   const { nresourecode,disabled } = props.selectedRecord;
 const { period} = props
    return (
        <Col md="12">
            <FormSelectSearch
                formLabel={props.intl.formatMessage({ id: "IDS_SUBCONTRACTOR" })}
                name={"nsuppliercode"}
                isDisabled={false}
                placeholder={props.intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                isMandatory={true}
                showOption={props.isMulti}
                options={props.subcontractor}
                optionId='nsuppliercode'
                optionValue='ssuppliername'
                defaultValue={props.selectedRecord["nsuppliercode"]}
                onChange={value => props.onComboChange(value, props.isMulti?"availableData":"nsuppliercode", 1)}
                value={props.selectedRecord["nsuppliercode"] ? props.selectedRecord["nsuppliercode"] || [] : []}
                isMulti={props.isMulti}
                isSearchable={true}
                closeMenuOnSelect={!props.isMulti}
                alphabeticalSort={true}
                isClearable={false}
            >
            </FormSelectSearch>

            {/* <FormInput
                    name={"nquantity"}
                    label={props.intl.formatMessage({ id: "IDS_QUANTITY" })}
                    type="text"
                    onChange={(event) => props.onInputOnChange(event, 4)}
                    placeholder={props.intl.formatMessage({ id: "IDS_QUANTITY" })}
                    value={props.selectedRecord && props.selectedRecord["nquantity"] ? props.selectedRecord["nquantity"] : ""}
                    isMandatory="*"
                    required={true}
                    maxLength={6}
                    precision={2}
                /> */}
                 <Row>
                <Col md="6">
                <FormNumericInput
                    name={"ncontrolleadtime"}
                    label={props.intl.formatMessage({ id: "IDS_CONTROLLEADTIME" })}
                    type="number"
                    value={props.selectedRecord["ncontrolleadtime"]}
                    placeholder={props.intl.formatMessage({ id: "IDS_CONTROLLEADTIME" })}
                    strict={true}
                    min={0}
                    //max={9999999.99}
                    maxLength={3}
                    onChange={(value) => props.onNumericInputChange(value, "ncontrolleadtime", "ncontrolleadtime")}
                    noStyle={true}
                    precision={0}
                    className="form-control"
                    errors="Please provide a valid number."
                /> 
                </Col>
                 <Col md="6">
                 <FormSelectSearch
                    formLabel={props.intl.formatMessage({ id: "IDS_PERIOD" })}
                    isSearchable={true}
                    name={"nperiodcode"}
                    placeholder={props.intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                    options={period || []}
                    optionId='nperiodcode'
                    optionValue='ssperiodname'
                    value={props.selectedRecord["nperiodcode"] || [] }
                    //value={props.selectedRecord["ncontrolleadtime"] ? props.selectedRecord["nperiodcode"] || [] : []}
                    onChange={value => props.onComboChange(value, "nperiodcode", 1)}
                    alphabeticalSort={true}
                    isMandatory={true}
                    isClearable={false}
                    isDisabled={false}
                >
                </FormSelectSearch>
                </Col>
                </Row>

        </Col>
    );
};

export default injectIntl(AddTestSubContractor);
