import React from 'react';
import { faPlus } from '@fortawesome/free-solid-svg-icons';
import { Nav, Row, Col } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { FormattedMessage } from 'react-intl';
import DataGrid from '../../components/data-grid/data-grid.component';
import { transactionStatus } from '../../components/Enumeration';

const TestSubContractorTab = (props) => {
    const addTestSubContractorId = props.controlMap.has("AddTestSubContractor") && props.controlMap.get("AddTestSubContractor").ncontrolcode;
    const testSubContractorColumnList = [
        {"idsName":"IDS_SUBCONTRACTOR","dataField":"ssuppliername","width":"100px"},
        {"idsName":"IDS_CONTROLLEADTIME","dataField":"scontrolleadtime","width":"100px"},
        // {"idsName":"IDS_PERIOD","dataField":"speriodname","width":"200px"},
        {"idsName":"IDS_SETASDEFAULT","dataField":"stransdisplaystatus","width":"100px", "componentName": "switch", 
        "switchFieldName": "ndefaultstatus", "switchStatus": transactionStatus.YES, "needRights": true, "controlName": "DefaultTestSubContractor"}

    ];    
    const methodUrl = "TestSubContractor";
    // const editId = props.inputParam && props.controlMap.has("Edit".concat(props.inputParam.methodUrl))
    //                     && props.controlMap.get("Edit".concat(props.inputParam.methodUrl)).ncontrolcode;
    const editParam = {screenName:"IDS_SUBCONTRACTOR" , operation:"update", 
    primaryKeyField:"ntestsubcontractorcode", inputParam:props.inputParam,  userInfo:props.userInfo
   // ,  ncontrolCode:editId
};


    return (
        <>
            <div className="actions-stripe">
                <div className="d-flex justify-content-end">
                    <Nav.Link name="addsubcontractor" className="add-txt-btn" 
                        hidden={props.userRoleControlRights.indexOf(addTestSubContractorId) === -1}
                        onClick={() => props.addTestSubContractor("create", props.selectedTest["ntestcode"], props.userInfo, addTestSubContractorId)}>
                        <FontAwesomeIcon icon={faPlus} /> {" "}
                        <FormattedMessage id="IDS_SUBCONTRACTOR" defaultMessage="SubContractor" />
                    </Nav.Link>
                </div>
            </div>
            <Row noGutters={true}>
                <Col md="12">
                    <DataGrid
                        key="testsubcontractorkey"
                        primaryKeyField="ntestsubcontractorcode"
                        data={props.TestSubContractor || []}
                        dataResult={props.dataResult}
                        dataState={props.dataState}
                        dataStateChange={props.dataStateChange}
                        extractedColumnList={testSubContractorColumnList}
                        controlMap={ props.controlMap }
                        userRoleControlRights={props.userRoleControlRights}
                        inputParam={props.inputParam}
                        userInfo={props.userInfo}
                        deleteRecord={props.deleteRecord}
                        pageable={true}
                        scrollable={'scrollable'}
                        gridHeight={'450px'}
                        isActionRequired={true}
                        deleteParam={{operation:"delete", methodUrl}}
                        methodUrl={methodUrl}
                        fetchRecord = {props.getActiveTestContainerTypeById}
                        editParam={editParam}
                        hideColumnFilter={false}
                        onSwitchChange={props.defaultRecord}
                        switchParam={{operation:"Default", methodUrl}}
                    >
                    </DataGrid>
                </Col>
            </Row>
        </>
    );
};

export default TestSubContractorTab;
