import { faPlus } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React, { Component } from 'react';
import { Col, Nav, Row } from 'react-bootstrap';
import { FormattedMessage, injectIntl } from 'react-intl';
import FormInput from '../../components/form-input/form-input.component';
import FormMultiSelect from '../../components/form-multi-select/form-multi-select.component';
import FormSelectSearch from '../../components/form-select-search/form-select-search.component';
import { HeaderSpan } from './registration.styled';

class AddSubSample extends Component {
    render() {
        return (
            <Row>
                <Col md={12}>
                    <FormSelectSearch
                        formLabel={this.props.intl.formatMessage({ id: "IDS_COMPONENT" })}
                        isSearchable={true}
                        name={"ncomponentcode"}
                        isDisabled={false}
                        placeholder={this.props.intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                        isMandatory={true}
                        options={this.props.Component}
                        alphabeticalSort="true"
                        optionId="ncomponentcode"
                        optionValue="scomponentname"
                        value={this.props.selectComponent ? this.props.selectComponent["ncomponentcode"] : ""}
                        defaultValue={this.props.selectComponent ? this.props.selectComponent["ncomponentcode"] : ""}
                        //  showOption={true}
                        closeMenuOnSelect={true}
                        onChange={(event) => this.props.onComponentChange(event, 'ncomponentcode')}>
                    </FormSelectSearch>
                    <FormInput
                        label={this.props.intl.formatMessage({ id: "IDS_SUBSAMPLENAME" })}
                        name="ssubsamplename"
                        type="text"
                        maxLength="100"
                        isMandatory={true}
                        value={this.props.selectComponent ? this.props.selectComponent["ssubsamplename"] : ""}
                        onChange={(event) => this.props.onInputComponentOnChange(event)}
                        placeholder={this.props.intl.formatMessage({ id: "IDS_SUBSAMPLENAME" })}
                        isDisabled={false}
                    />


                    <Row>
                        <Col md={'6'}>
                            <FormInput
                                label={this.props.intl.formatMessage({ id: "IDS_TOTALQUANTITY" })}
                                name="ssampleqty"
                                type="text"
                                maxLength="100"
                                isMandatory={true}
                                value={this.props.selectComponent ? this.props.selectComponent["ssampleqty"] : ""}
                                onChange={(event) => this.props.onInputComponentOnChange(event)}
                                placeholder={this.props.intl.formatMessage({ id: "IDS_TOTALQUANTITY" })}
                                isDisabled={false}
                            />
                        </Col>
                        <Col md={'6'}>

                            <FormSelectSearch
                                formLabel={this.props.intl.formatMessage({ id: "IDS_UNIT" })}
                                isSearchable={true}
                                name={"nunitcode"}
                                isDisabled={false}
                                placeholder={this.props.intl.formatMessage({ id: "IDS_UNIT" })}
                                isMandatory={true}
                                options={this.props.Unit || []}
                                alphabeticalSort="true"
                                optionId="nunitcode"
                                optionValue="sunitname"
                                value={this.props.selectComponent ? this.props.selectComponent["nunitcode"] : ""}
                                defaultValue={this.props.selectComponent ? this.props.selectComponent["nunitcode"] : ""}
                                //  showOption={true}
                                closeMenuOnSelect={true}
                                onChange={(event) => this.props.onComponentChange(event, 'nunitcode', true)}>
                            </FormSelectSearch>
                        </Col>
                    </Row>
                    <FormInput
                        label={this.props.intl.formatMessage({ id: "IDS_COMMENTS" })}
                        name="scomments"
                        type="text"
                        maxLength="100"
                        isMandatory={true}
                        value={this.props.selectComponent ? this.props.selectComponent["scomments"] : ""}
                        onChange={(event) => this.props.onInputComponentOnChange(event)}
                        placeholder={this.props.intl.formatMessage({ id: "IDS_COMMENTS" })}
                        isDisabled={false}
                    />
                </Col>
                <Col md={12}>
                    <Row noGutters={true}>
                        {/* <Row noGutters={true}> */}
                        <Col md={12} className="p-0">
                            <div className="actions-stripe">
                                <div className="d-flex justify-content-end">
                                    <div style={{ "bottom": "20px" }}>
                                        <HeaderSpan style={{ "bottom": "20px" }}>

                                            <FormattedMessage id='IDS_TEST' defaultMessage='Test' />

                                        </HeaderSpan>
                                    </div>
                                    <Nav.Link className="add-txt-btn text-left"
                                    //onClick={(e) => this.props.AddSpec(e)}
                                    >
                                        {/* <FontAwesomeIcon icon={faPlus} /> { }{ } */}
                                        {/* <FormattedMessage id='IDS_TEST' defaultMessage='Test' /> */}
                                    </Nav.Link>
                                </div>
                            </div>
                        </Col>
                    </Row>
                </Col>
                <Col md={12}>
                    <FormMultiSelect
                        name={"ntestgrouptestcode"}
                        label={this.props.intl.formatMessage({ id: "IDS_TESTNAME" })}
                        options={this.props.TestCombined || []}
                        optionId={"ntestgrouptestcode"}
                        optionValue="stestsynonym"
                        value={this.props.selectedTestData && this.props.selectedTestData["ntestgrouptestcode"] ?
                            this.props.selectedTestData["ntestgrouptestcode"] : []}
                        isMandatory={true}
                        isClearable={true}
                        disableSearch={false}
                        disabled={false}
                        closeMenuOnSelect={false}
                        alphabeticalSort={true}
                        onChange={(event) => this.props.TestChange(event, "ntestgrouptestcode")}

                    />
                </Col>
            </Row>
        );
    }
}

export default injectIntl(AddSubSample);
