import rsapi from '../rsapi';
import { DEFAULT_RETURN } from './LoginTypes';
import { toast } from 'react-toastify';
import { initRequest } from './LoginAction';
import { sortData, constructjsonOptionList, constructOptionList, parentChildComboLoad } from '../components/CommonScript';
import { getValidComponent, replaceChildFromChildren } from '../components/droparea/helpers';
import { ReactComponents, transactionStatus } from '../components/Enumeration';
import Axios from 'axios';

export function getReactInputFields(userinfo, operation, respObj) {

    return function (dispatch) {
        dispatch(initRequest(true));
        const listURL = [];
        listURL[0] = rsapi.post("dynamicpreregdesign/getReactComponents", { userinfo })
        listURL[1] = rsapi.post("dynamicpreregdesign/getReactInputFields", { userinfo })
        Axios.all(listURL)
            .then(response => {
                respObj = respObj ? { ...respObj } : {}
                dispatch({
                    type: DEFAULT_RETURN,
                    payload: {
                        ReactInputFields: response[1].data,
                        ReactComponents: response[0].data.components,
                        ReactTables: constructOptionList(response[0].data.tables, 'stablename', 'displayname').get("OptionList"),
                        selectedFieldRecord: {},
                        openModal: false,
                        loading: false,
                        openPortal: true,
                        operation,
                        ...respObj
                    }
                })
            })
            .catch(error => {
                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
                if (error.response.status === 500) {
                    toast.error(error.message);
                } else {
                    toast.warn(error.response.data);
                }
            })
    }
}
export function selectRegistrationTemplate(template, masterData, userinfo) {
    return function (dispatch) {
        dispatch(initRequest(true));
        rsapi.post("dynamicpreregdesign/getRegistrationTemplateById", {
            nreactregtemplatecode: template.nreactregtemplatecode,
            userinfo
        })
            .then(response => {

                dispatch({
                    type: DEFAULT_RETURN,
                    payload: { masterData: { ...masterData, selectedTemplate: response.data }, loading: false }
                })
            })
            .catch(error => {
                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
                if (error.response.status === 500) {
                    toast.error(error.message);
                } else {
                    toast.warn(error.response.data);
                }
            })
    }
}
export function getRegistrationTemplate(selectedSampleType, masterData, userinfo) {
    return function (dispatch) {
        dispatch(initRequest(true));
        rsapi.post("dynamicpreregdesign/getDynamicPreRegDesign", {
            nsampletypecode: selectedSampleType.value,
            userinfo
        })
            .then(response => {
                masterData = { ...masterData, ...response.data, searchedData: undefined }
                sortData(masterData)
                dispatch({
                    type: DEFAULT_RETURN,
                    payload: {
                        masterData,
                        realSampleType: [{
                            "label": "IDS_SAMPLETYPE",
                            "value": selectedSampleType.label,
                            "item": selectedSampleType
                        }],
                        loading: false,

                    }
                })
            })
            .catch(error => {
                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
                if (error.response.status === 500) {
                    toast.error(error.message);
                } else {
                    toast.warn(error.response.data);
                }
            })
    }
}
export function getEditRegTemplate(masterData, userinfo, editId) {
    return function (dispatch) {
        dispatch(initRequest(true));
        rsapi.post("dynamicpreregdesign/getRegistrationTemplateById", {
            nreactregtemplatecode: masterData.selectedTemplate.nreactregtemplatecode,
            userinfo
        })
            .then(response => {
                if (response.data.ntransactionstatus == transactionStatus.DRAFT) {
                    let jsonData = response.data.jsondata;
                    sortData(masterData)

                    const respObj = {
                        masterData,
                        ncontrolcode: editId,
                        openPortal: true,
                        loading: false,
                        openModal: false,
                        design: jsonData,
                        selectedRecord: { templateName: response.data.sregtemplatename }
                    }
                    // dispatch({
                    //     type: DEFAULT_RETURN,
                    //     payload: {
                    //         masterData,
                    //         ncontrolcode: editId,
                    //         // openPortal: true,
                    //         // loading: false,
                    //         design: jsonData,
                    //         selectedRecord: { templateName: response.data.sregtemplatename }
                    //     }
                    // })
                    dispatch(getReactInputFields(userinfo, "update", respObj))
                } else {
                    dispatch({ type: DEFAULT_RETURN, payload: { loading: false, masterStatus: "IDS_SELECTDRAFTRECORD" } })
                }
            })
            .catch(error => {
                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
                if (error.response.status === 500) {
                    toast.error(error.message);
                } else {
                    toast.warn(error.response.data);
                }
            })
    }
}

export function getJsonValue(PGjsonData) {

    if (Array.isArray(PGjsonData)) {
        let JSONData = [];
        PGjsonData.map(data => {
            JSONData.push(JSON.parse(data.jsondata))
        })
        return JSONData;
    } else {
        return JSON.parse(PGjsonData);
    }

}
export function getTableColumns(design, selectedFieldRecord, stablename, userinfo, path, columnInfo, componentData, updateComponents) {
    return function (dispatch) {
        dispatch(initRequest(true));
        rsapi.post("dynamicpreregdesign/getTableColumns", { stablename, userinfo })
            .then(response => {
                columnInfo = columnInfo || {}
                const staicColumns = response.data.jstaticcolumns || []
                const dynamicColumns = response.data.jdynamiccolumns || []
                const multilingualColumns = response.data.jmultilingualcolumn || []
                const numericColumns = response.data.jnumericcolumns || []
                const primaryKeyName = response.data.sprimarykeyname
                columnInfo = {
                    ...columnInfo,
                    [stablename]: {
                        staicColumns,
                        dynamicColumns,
                        multilingualColumns,
                        numericColumns,
                        primaryKeyName
                    }
                }
                let defaultColumn = {};
                let comboData = []
                staicColumns.map(item => {
                    comboData.push({
                        label: item.displayname[userinfo.slanguagetypecode],
                        value: item.columnname,
                        item
                    })
                    if (item.default) {
                        defaultColumn = {
                            label: item.displayname[userinfo.slanguagetypecode],
                            value: item.columnname,
                            item
                        }
                    }
                })
                dynamicColumns.map(item => {
                    comboData.push({
                        label: item.displayname[userinfo.slanguagetypecode],
                        value: item.columnname,
                        item
                    })
                    if (item.default) {
                        defaultColumn = {
                            label: item.displayname[userinfo.slanguagetypecode],
                            value: item.columnname,
                            item
                        }
                    }
                })
                multilingualColumns.map(item => {
                    comboData.push({
                        label: item.displayname[userinfo.slanguagetypecode],
                        value: item.columnname,
                        item
                    })
                    if (item.default) {
                        defaultColumn = {
                            label: item.displayname[userinfo.slanguagetypecode],
                            value: item.columnname,
                            item
                        }
                    }
                })
                if (updateComponents) {
                    componentData = componentData || {}
                    selectedFieldRecord = { ...selectedFieldRecord, column: defaultColumn, displaymember: defaultColumn.value, valuemember: primaryKeyName };
                    design = replaceChildFromChildren(design, path, selectedFieldRecord)
                    if (selectedFieldRecord.componentcode !== ReactComponents.COMBO) {
                        let validComponents = getValidComponent(selectedFieldRecord, componentData.components, columnInfo);
                        componentData = { ...componentData, validComponents }
                    }
                } else if (componentData === undefined) {
                    componentData = componentData || {}
                    selectedFieldRecord = { ...selectedFieldRecord, column: defaultColumn, displaymember: defaultColumn.value, valuemember: primaryKeyName };
                    design = replaceChildFromChildren(design, path, selectedFieldRecord)
                    if (selectedFieldRecord.componentcode !== ReactComponents.COMBO) {
                        let validComponents = getValidComponent(selectedFieldRecord, componentData.components, columnInfo);
                        componentData = { ...componentData, validComponents }
                    }
                } else {
                    let validComponents = getValidComponent(selectedFieldRecord, componentData.components, columnInfo);
                    componentData = { ...componentData, validComponents }
                }
                columnInfo = {
                    ...columnInfo,
                    [stablename]: { ...columnInfo[stablename], tableColumn: comboData }
                }
                dispatch({
                    type: DEFAULT_RETURN,
                    payload: {
                        loading: false,
                        design,
                        selectedFieldRecord,
                        tableColumn: comboData,
                        columnInfo,
                        primaryKeyName,
                        ...componentData
                    }
                })
            })
            .catch(error => {
                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
                if (error.response.status === 500) {
                    toast.error(error.message);
                } else {
                    toast.warn(error.response.data);
                }
            })
    }
}

export function getPreviewTemplate(masterData, userinfo,
    editId, columnList, selectedRecord, childColumnList, comboComponents, withoutCombocomponent) {
    return function (dispatch) {
        dispatch(initRequest(true));
        const timeZoneService = rsapi.post("timezone/getTimeZone");
        const actualService = rsapi.post("dynamicpreregdesign/getComboValues", {
            parentcolumnlist: columnList,
            childcolumnlist: childColumnList,
            userinfo
        })
        Axios.all([timeZoneService, actualService])
            .then(response => {
                const timeZoneMap = constructOptionList(response[0].data || [], "ntimezonecode", "stimezoneid", undefined, undefined, true);
                const timeZoneList = timeZoneMap.get("OptionList");
                const defaultTimeZone = { label: userinfo.stimezoneid, value: userinfo.ntimezonecode }
                const newcomboData = parentChildComboLoad(columnList, response[1].data, selectedRecord, childColumnList, withoutCombocomponent)
                sortData(masterData)
                dispatch({
                    type: DEFAULT_RETURN,
                    payload: {
                        timeZoneList,
                        defaultTimeZone,
                        masterData,
                        ncontrolcode: editId,
                        openModal: true,
                        loading: false,
                        comboData: newcomboData.comboData,
                        selectedRecord: newcomboData.selectedRecord,
                        comboComponents,
                        withoutCombocomponent
                    }
                })
            })
            .catch(error => {
                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
                if (error.response.status === 500) {
                    toast.error(error.message);
                } else {
                    toast.warn(error.response.data);
                }
            })
    }
}

export function getChildValues(inputParem,
