import Axios from "axios";
import { toast } from "react-toastify";
import { intl } from "../components/App";
import { constructOptionList, replaceUpdatedObject, sortData } from "../components/CommonScript";
import { reportCOAType, REPORTTYPE } from "../components/Enumeration";
import TrainingUpdateChildTab from "../pages/competencemanagement/trainingupdate/TrainingUpdateChildTab";
import rsapi from "../rsapi";
import { initRequest } from "./LoginAction";
import { DEFAULT_RETURN } from "./LoginTypes";

export function getReleasedSelectedSampleSubSampleTest(userInfo, Data1, inputData) {


    return function (dispatch) {
      if (inputData !== undefined &&inputData.npreregno!=="") {

        dispatch(initRequest(true));
        return rsapi.post("release/updateRelease",

            inputData
        )
            .then(response => {
                if (response.data.statusCodeValue!==undefined&&response.data.statusCodeValue !== 200) {
                    toast.warn(intl.formatMessage({
                        id: response.data
                    }));
                    dispatch({
                        type: DEFAULT_RETURN,
                        payload: {
                            loading: false
                        }
                    });


                }
                else {
         
                    if (response.data["PortalStatus"] && response.data["PortalStatus"].length > 0) {
                        dispatch(UpdateExternalOrderStatus(response.data["PortalStatus"],inputData));
                    }
                    let masterData = {

                        ...Data1, ...response.data,
    

                    };

                    console.log("ReleasedTest,", masterData);
                     sortData(masterData.ReleaseHistory,"",'sreportno');
                  //  dispatch(generateReport(inputData,masterData))

                    dispatch({
                        type: DEFAULT_RETURN, payload: {
                            masterData,
                            change: inputData.change,
                            loading: false,
                            openModal:false
                        }
                    });
                }
            })

            .catch(error => {
                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
                if (error.response.status === 500) {
                    toast.error(error.message);
                    console.log("ErrorRelease,", error);

                }
                else {
                    toast.warn(error.response.data);
                }
            })


        }
        else {
            toast.warn(intl.formatMessage({
                id: "IDS_SELECTANYONESAMPLE"
            }));
        }
    }
}
