  componentDidUpdate(previousProps) {
    if (this.props.Login.selectedRecord !== previousProps.Login.selectedRecord) {
      this.setState({
        selectedRecord: this.props.Login.selectedRecord
      });
    }

    if (this.props.Login.masterData !== previousProps.Login.masterData) {
      let fields = this.state.fields;
      let queryBuilderGender = [];
      let queryBuilderCity = [];
      let queryBuilderCountry = [];
      this.props.Login.masterData.genderList && this.props.Login.masterData.genderList.map(item => queryBuilderGender.push({
        "value": item.ngendercode,
        "title": item.sgendername
      }));
      this.props.Login.masterData.cityList && this.props.Login.masterData.cityList.map(item => queryBuilderCity.push({
        "value": item.ncitycode,
        "title": item.scityname
      }));
      this.props.Login.masterData.countryList && this.props.Login.masterData.countryList.map(item => queryBuilderCountry.push({
        "value": item.ncountrycode,
        "title": item.scountryname
      })); //this.setState({ queryArray: queryArray });

      fields = { ...fields,
        // "patientmaster.ngendercode": {
