        "width": "200px",
        "mandatory": true,
        "mandatoryLabel": "IDS_SELECT",
        "controlType": "selectbox"
      }, {
        "idsName": "IDS_USER",
        "dataField": "nusercode",
        "width": "200px",
        "mandatory": true,
        "mandatoryLabel": "IDS_SELECT",
        "controlType": "selectbox"
      }, {
        "idsName": "IDS_RFWDATE",
        "dataField": "drfwdate",
        "width": "200px",
        "mandatory": true,
        "mandatoryLabel": "IDS_CHOOSE",
        "controlType": "selectbox"
      }, {
        "idsName": "IDS_STARTDATE",
        "dataField": "dprojectstartdate",
        "width": "200px",
        "mandatory": true,
        "mandatoryLabel": "IDS_CHOOSE",
        "controlType": "selectbox"
      }, {
        "idsName": "IDS_PROJECTDURATION",
        "dataField": "nprojectduration",
        "width": "200px",
