        "mandatoryLabel": "IDS_SELECT",
        "controlType": "selectbox"
      }];
      mandatoryFields.forEach(item => item.mandatory === true && mandatoryFields.push(item));
      return mandatoryFields;
    } else if (screenName === "IDS_CLOSUREFILE") {
      mandatoryFields = [{
        "idsName": "IDS_FILENAME",
        "dataField": "sfilename",
        "mandatory": true,
        "mandatoryLabel": "IDS_CHOOSE",
        "controlType": "file"
      }, {
        "idsName": "IDS_REASONFORCLOSURE",
        "dataField": "sclosureremarks",
        "width": "200px",
        "mandatory": true,
        "mandatoryLabel": "IDS_ENTER",
        "controlType": "textarea"
